import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3001', // Ensure this matches your backend server URL
  timeout: 10000, // Set a 10-second timeout for requests
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add a response interceptor to handle errors globally
api.interceptors.response.use(
  response => response,
  error => {
    // Provide more specific error handling
    if (error.code === 'ECONNABORTED') {
      return Promise.reject(new Error('Request timed out. Please check if the server is running.'));
    }
    if (!error.response) {
      return Promise.reject(new Error('Network error. Please check your internet connection or server status.'));
    }
    return Promise.reject(error);
  }
);

export default api;
