import React, { useEffect, useState } from 'react';
import api from '../api';

function Sales({ products: initialProducts }) {
  const [salesData, setSalesData] = useState([]);
  const [totalSales, setTotalSales] = useState(0);
  const [filterDate, setFilterDate] = useState('');
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [products, setProducts] = useState(initialProducts || []);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setProducts(initialProducts || []);
    setLoading(false);
  }, [initialProducts]);

  const fetchSales = async () => {
    try {
      const res = await api.get('/sales' + (filterDate ? `?date=${filterDate}` : ''));
      setSalesData(res.data || []);
      setTotalSales(res.data.reduce((sum, sale) => sum + sale.amount, 0));
    } catch (err) {
      console.error('Error fetching sales:', err);
    }
  };

  useEffect(() => {
    fetchSales();
  }, [filterDate]);

  const sellProduct = async (e) => {
    e.preventDefault();
    if (!selectedProduct || quantity <= 0) {
      alert('Please select a product and enter a valid quantity.');
      return;
    }

    const product = products.find(p => p.id === selectedProduct);
    if (!product || product.stock < quantity) {
      alert('Insufficient stock or invalid product.');
      return;
    }

    try {
      const saleAmount = product.price * quantity;
      const saleData = {
        productId: selectedProduct,
        name: product.name,
        quantity: quantity,
        amount: saleAmount,
        date: new Date().toISOString().split('T')[0],
      };

      await api.post('/sales', saleData);

      const updatedProducts = products.map(p =>
        p.id === selectedProduct ? { ...p, stock: p.stock - quantity } : p
      );
      setProducts(updatedProducts);

      const updatedUIData = JSON.parse(localStorage.getItem('uiData') || '[]').map(p =>
        p.id === selectedProduct ? { ...p, stock: p.stock - quantity } : p
      );
      localStorage.setItem('uiData', JSON.stringify(updatedUIData));

      setSalesData([...salesData, saleData]);
      setTotalSales(totalSales + saleAmount);
      setQuantity(1);
      setSelectedProduct('');
    } catch (err) {
      console.error('Error processing sale:', err);
      alert('Failed to record sale. Please try again.');
    }
  };

  if (loading) return <div className="dashboard">Loading sales...</div>;

  return (
    <div className="dashboard">
      <div className="card">
        <h3>Sales Overview</h3>
        <div style={{ marginBottom: '15px' }}>
          <label>Filter by Date: </label>
          <input
            type="date"
            value={filterDate}
            onChange={(e) => setFilterDate(e.target.value)}
          />
        </div>
        <p>Total Sales: ${totalSales.toFixed(2)}</p>
      </div>
      <div className="card">
        <h3>Sell Product</h3>
        <form onSubmit={sellProduct} className="add-product-form">
          <select
            name="selectedProduct"
            value={selectedProduct}
            onChange={(e) => setSelectedProduct(e.target.value)}
            required
          >
            <option value="">Select a Product</option>
            {products.map(product => (
              <option key={product.id} value={product.id}>
                {product.name} (Stock: {product.stock})
              </option>
            ))}
          </select>
          <input
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
            min="1"
            placeholder="Quantity"
            required
          />
          <button type="submit">Sell</button>
        </form>
      </div>
      <div className="card">
        <h3>Sales Transactions</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Product</th>
              <th>Quantity</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {salesData.map(sale => (
              <tr key={sale.productId + sale.date}>
                <td>{new Date(sale.date).toLocaleDateString()}</td>
                <td>{sale.name}</td>
                <td>{sale.quantity}</td>
                <td>${sale.amount.toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Sales;
