import React, { useState, useEffect } from 'react';
import api from '../api';

function Sales({ products = [], updateProductQuantityOrPrice, onSold }) {
  const [selling, setSelling] = useState({});
  const [isSubmitting, setIsSubmitting] = useState({}); // track per-product submission state

  // Initialize selling quantity to 1 for all existing/new products
  useEffect(() => {
    const init = {};
    products.forEach(p => {
      init[p.id] = 1;
    });
    setSelling(init);
  }, [products]);

  const handleQuantityChange = (productId, value) => {
    const product = products.find(p => p.id === productId);
    if (!product) return;
    const allowed = Number(product.quantity || 0);
    let num = parseInt(value, 10);
    if (isNaN(num) || num < 1) num = 1;
    if (num > allowed) num = allowed;
    setSelling(prev => ({ ...prev, [productId]: num }));
  };

  const sellProduct = async (productId) => {
    const qtyToSell = selling[productId] || 1;
    const product = products.find(p => p.id === productId);
    if (!product) {
      alert('Product not found');
      return;
    }
    if (qtyToSell > product.quantity) {
      alert(`Quantity (${qtyToSell}) exceeds available quantity (${product.quantity}).`);
      return;
    }

    setIsSubmitting(prev => ({ ...prev, [productId]: true }));

    try {
      console.log('Attempting sale:', { productId, qtyToSell, currentQuantity: product.quantity });

      // Update product quantity
      const updatedQuantity = product.quantity - qtyToSell;
      const updatedProduct = { ...product, quantity: updatedQuantity };

      const response = await api.put(`/products/${productId}`, updatedProduct);
      console.log('PUT response:', response);

      updateProductQuantityOrPrice(productId, { quantity: updatedQuantity });
      if (onSold) onSold();

      setSelling(prev => ({ ...prev, [productId]: 1 }));
      alert(`Sold ${qtyToSell} x ${product.name} successfully! Total: M${(product.price * qtyToSell).toFixed(2)}`);
    } catch (err) {
      console.error('Sale Error:', {
        productId,
        message: err.message,
        status: err.response?.status,
        responseData: err.response?.data,
        requestInfo: err.request,
      });

      if (err.response) {
        switch (err.response.status) {
          case 400:
            alert('Invalid sale data. Please check the quantity.');
            break;
          case 404:
            alert('Product was not found on server.');
            break;
          case 500:
            alert('Internal server error. Try again later.');
            break;
          default:
            alert(`Sale failed: ${err.response.data?.message || 'Unknown server error'}`);
        }
      } else {
        // No response from server
        alert('Network error. Unable to reach server at http://localhost:3001. Check server and your network.');
      }
    } finally {
      setIsSubmitting(prev => ({ ...prev, [productId]: false }));
    }
  };

  if (products.length === 0) return <div>No products available</div>;

  return (
    <div className="dashboard">
      <h3>Sales</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 15 }}>
        {products.map(product => (
          <div
            key={product.id}
            className="card"
            style={{ width: 250, padding: 15, border: '1px solid #ccc', borderRadius: 8 }}
          >
            <h4>{product.name}</h4>
            <p>Price: M{Number(product.price || 0).toFixed(2)}</p>
            <p>Quantity: {product.quantity || 0}</p>
            <input
              type="number"
              min="1"
              max={product.quantity || 0}
              value={selling[product.id] || 1}
              onChange={e => handleQuantityChange(product.id, e.target.value)}
              disabled={product.quantity === 0 || isSubmitting[product.id]}
              style={{ width: '80px', marginBottom: 8 }}
            />
            <button
              onClick={() => sellProduct(product.id)}
              disabled={product.quantity === 0 || isSubmitting[product.id]}
              style={{
                width: '100%',
                backgroundColor: product.quantity === 0 || isSubmitting[product.id] ? '#ccc' : '#007bff',
                color: '#fff',
                border: 'none',
                padding: '8px',
                cursor: product.quantity === 0 || isSubmitting[product.id] ? 'not-allowed' : 'pointer',
              }}
            >
              {isSubmitting[product.id] ? 'Processing...' : product.quantity === 0 ? 'Out of Stock' : 'Sell'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Sales;
