import React from 'react';

function Report({ products = [], sales = [] }) {
  // Calculate total sales amount
  const totalSalesAmount = sales.reduce((sum, sale) => sum + sale.totalAmount, 0);

  // Calculate total quantity sold per product
  const salesByProduct = sales.reduce((acc, sale) => {
    acc[sale.productId] = (acc[sale.productId] || 0) + sale.quantity;
    return acc;
  }, {});

  return (
    <div className="dashboard">
      <h3>Sales & Inventory Report</h3>

      {/* Summary */}
      <div style={{ marginBottom: 20 }}>
        <h4>Total Sales Amount: M{totalSalesAmount.toFixed(2)}</h4>
        <h4>Products Available:</h4>
        {products.length === 0 ? (
          <p>No products available.</p>
        ) : (
          <ul>
            {products.map(product => (
              <li key={product.id}>
                <strong>{product.name}</strong>: {product.stock} in stock @ M{product.price.toFixed(2)} each
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Detailed Table */}
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th style={{ border: '1px solid #ccc', padding: 8 }}>Product</th>
            <th style={{ border: '1px solid #ccc', padding: 8 }}>Price (M)</th>
            <th style={{ border: '1px solid #ccc', padding: 8 }}>Stock Left</th>
            <th style={{ border: '1px solid #ccc', padding: 8 }}>Quantity Sold</th>
            <th style={{ border: '1px solid #ccc', padding: 8 }}>Sales Amount (M)</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => {
            const qtySold = salesByProduct[product.id] || 0;
            const salesAmount = qtySold * product.price;
            return (
              <tr key={product.id}>
                <td style={{ border: '1px solid #ccc', padding: 8 }}>{product.name}</td>
                <td style={{ border: '1px solid #ccc', padding: 8 }}>M{product.price.toFixed(2)}</td>
                <td style={{ border: '1px solid #ccc', padding: 8 }}>{product.stock}</td>
                <td style={{ border: '1px solid #ccc', padding: 8 }}>{qtySold}</td>
                <td style={{ border: '1px solid #ccc', padding: 8 }}>M{salesAmount.toFixed(2)}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default Report;
