import React, { useState } from 'react';
import api from '../api';

export default function AddProductForm({ onAdded, addUIData }) {
  const [product, setProduct] = useState({ name: '', description: '', category: '', price: '', quantity: '' });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => setProduct(p => ({ ...p, [e.target.name]: e.target.value }));

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await api.post('/products', { ...product, price: Number(product.price), quantity: Number(product.quantity) });
      setProduct({ name: '', description: '', category: '', price: '', quantity: '' });
      if (onAdded) onAdded();
      if (addUIData) addUIData({ ...product, price: Number(product.price), quantity: Number(product.quantity) });
      alert('Product added to both databases');
    } catch (err) {
      console.error(err);
      alert('Failed to add product');
    } finally { setLoading(false); }
  };

  return (
    <form onSubmit={handleSubmit} className="card">
      <h3>Add Product</h3>
      <input name="name" placeholder="Name" value={product.name} onChange={handleChange} required />
      <input name="description" placeholder="Description" value={product.description} onChange={handleChange} />
      <input name="category" placeholder="Category" value={product.category} onChange={handleChange} />
      <input name="price" placeholder="Price" type="number" step="0.01" value={product.price} onChange={handleChange} required />
      <input name="quantity" placeholder="Quantity" type="number" value={product.quantity} onChange={handleChange} required />
      <button type="submit" disabled={loading}>{loading ? 'Adding...' : 'Add Product'}</button>
    </form>
  );
}
