import React from 'react';

function Dashboard({ products }) {
  if (!products.length) return <div className="dashboard">No products available</div>;

  return (
    <div className="dashboard">
      <h3>Dashboard Overview</h3>
      <div className="card dashboard-table">
        <table className="table product-dashboard">
          <thead>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Category</th>
              <th>Price (M)</th>
              <th>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {products.map(product => {
              const isLowStock = Number(product.quantity || 0) <= 5; // Threshold of 5
              return (
                <tr
                  key={product.id}
                  style={{ backgroundColor: isLowStock ? '#ffcccc' : 'transparent' }} // Light red for low stock
                >
                  <td>{product.name}</td>
                  <td>{product.description || 'N/A'}</td>
                  <td>{product.category || 'N/A'}</td>
                  <td>{`M${product.price.toFixed(2)}`}</td>
                  <td>{product.quantity || 0}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Dashboard;
