import React, { useState, useEffect } from 'react';
// eslint-disable-next-line no-unused-vars
import api from '../api'; // Kept with suppression

export default function Dashboard({ products: initialProducts = [], onStockChange }) {
  const [products, setProducts] = useState(initialProducts);
  const [editStock, setEditStock] = useState({}); // { productId: editingValue }

  useEffect(() => {
    setProducts(initialProducts);
  }, [initialProducts]);

  const totalItems = products.length;
  const totalStock = products.reduce((s, p) => s + Number(p.quantity || 0), 0);
  const lowStock = products.filter(p => Number(p.quantity || 0) <= 5).length;

  const handleImageError = (e) => {
    e.target.src = 'https://via.placeholder.com/150?text=Image+Not+Available';
  };

  const handleStockChange = (id, value) => {
    if (/^\d*$/.test(value)) {
      setEditStock(prev => ({ ...prev, [id]: value }));
    }
  };

  return (
    <div className="dashboard card" style={{ padding: '10px' }}>
      <h3 style={{ fontSize: '1.5rem' }}>Dashboard Overview</h3>
      <div
        className="summary-cards"
        style={{
          display: 'flex',
          gap: '1rem',
          flexDirection: 'column',
          marginBottom: '1rem',
        }}
      >
        <div className="summary-card" style={{ flex: 1, padding: '10px' }}>
          <h4 style={{ fontSize: '1rem' }}>Total Items</h4>
          <p><strong style={{ fontSize: '1.2rem' }}>{totalItems}</strong></p>
        </div>
        <div className="summary-card" style={{ flex: 1, padding: '10px' }}>
          <h4 style={{ fontSize: '1rem' }}>Total Stock</h4>
          <p><strong style={{ fontSize: '1.2rem' }}>{totalStock}</strong></p>
        </div>
        <div className="summary-card" style={{ flex: 1, padding: '10px' }}>
          <h4 style={{ fontSize: '1rem' }}>Low Stock</h4>
          <p><strong style={{ fontSize: '1.2rem' }}>{lowStock}</strong></p>
        </div>
      </div>

      <div
        className="product-cards"
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          gap: '10px',
          marginTop: '1rem',
        }}
      >
        {products.slice(0, 5).map(p => {
          const isLowStock = Number(p.quantity || 0) <= 5; // Threshold of 5
          const currentQuantity = editStock[p.id] !== undefined ? editStock[p.id] : p.quantity;
          return (
            <div
              key={p.id}
              className="product-card"
              style={{
                border: '1px solid #ddd',
                padding: '5px',
                width: '100%', // Full width on mobile
                boxSizing: 'border-box',
                backgroundColor: isLowStock ? '#ffcccc' : 'transparent',
              }}
            >
              <img
                src={p.image || 'https://via.placeholder.com/150?text=No+Image'}
                alt={p.name}
                className="product-image"
                style={{
                  width: '100%',
                  height: '100px', // Reduced height for mobile
                  objectFit: 'cover',
                }}
                onError={handleImageError}
              />
              <div className="product-info" style={{ marginTop: '5px' }}>
                <h4 style={{ fontSize: '1rem', margin: '5px 0' }}>{p.name}</h4>
                <p style={{ fontSize: '0.9rem', margin: '5px 0' }}>Price: M{Number(p.price || 0).toFixed(2)}</p>
                <div>
                  <label style={{ fontSize: '0.9rem' }}>
                    Quantity:&nbsp;
                    <input
                      type="text"
                      value={currentQuantity}
                      onChange={(e) => handleStockChange(p.id, e.target.value)}
                      style={{ width: '50px', fontSize: '0.9rem' }}
                      readOnly
                    />
                  </label>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
