import React, { useState, useEffect } from 'react';
import api from '../api';

function Inventory({ products: initialProducts, onChanged }) {
  const [products, setProducts] = useState(initialProducts || []);
  const [newProduct, setNewProduct] = useState({ name: '', price: '', stock: '' });
  const [uiData, setUIData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setProducts(initialProducts || []);
    setLoading(false);
  }, [initialProducts]);

  const fetchProducts = async () => {
    try {
      const res = await api.get('/products');
      const staticProducts = res.data || [];
      const savedUIData = localStorage.getItem('uiData') ? JSON.parse(localStorage.getItem('uiData')) : [];
      setUIData(savedUIData);

      const merged = [...staticProducts];
      savedUIData.forEach(uiProduct => {
        if (!merged.some(p => p.id === uiProduct.id || (p.name === uiProduct.name && p.price === uiProduct.price))) {
          merged.push({ ...uiProduct, id: uiProduct.id || Date.now() + Math.random().toString(36).substr(2, 9) });
        }
      });
      setProducts(merged);
      if (onChanged) onChanged();
    } catch (err) {
      console.error('Error fetching products:', err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct(prev => ({ ...prev, [name]: value }));
  };

  const addProduct = async (e) => {
    e.preventDefault();
    try {
      const productToAdd = {
        name: newProduct.name,
        price: parseFloat(newProduct.price),
        stock: parseInt(newProduct.stock, 10),
      };
      const res = await api.post('/products', productToAdd);
      const updatedStatic = res.data;
      const updatedUIData = [...uiData, { ...productToAdd, id: updatedStatic.id, timestamp: new Date().toISOString() }];
      setUIData(updatedUIData);
      localStorage.setItem('uiData', JSON.stringify(updatedUIData));
      setNewProduct({ name: '', price: '', stock: '' }); // Clear form
      fetchProducts();
    } catch (err) {
      console.error(err);
      alert('Failed to add to static database, adding to UI only');
      const updatedUIData = [...uiData, { ...newProduct, id: Date.now(), timestamp: new Date().toISOString() }];
      setUIData(updatedUIData);
      localStorage.setItem('uiData', JSON.stringify(updatedUIData));
      setNewProduct({ name: '', price: '', stock: '' }); // Clear form
      fetchProducts();
    }
  };

  const exportUIData = () => {
    const dataStr = JSON.stringify(uiData, null, 2);
    const blob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'uiData.json';
    link.click();
    URL.revokeObjectURL(url);
  };

  if (loading) return <div className="dashboard">Loading inventory...</div>;

  return (
    <div className="dashboard">
      <div className="card inventory-table">
        <h3>Product Inventory</h3>
        <table className="table product-inventory">
          <thead>
            <tr>
              <th>Name</th>
              <th>Price</th>
              <th>Stock</th>
            </tr>
          </thead>
          <tbody>
            {products.map(product => (
              <tr key={product.id} className={product.stock < 10 ? 'low' : ''}>
                <td>{product.name}</td>
                <td>${parseFloat(product.price).toFixed(2)}</td>
                <td>{product.stock}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="card inventory-form" style={{ marginTop: '20px' }}>
        <h3>Add New Product</h3>
        <form onSubmit={addProduct} className="add-product-form">
          <input
            type="text"
            name="name"
            value={newProduct.name}
            onChange={handleInputChange}
            placeholder="Product Name"
            required
          />
          <input
            type="number"
            name="price"
            value={newProduct.price}
            onChange={handleInputChange}
            placeholder="Price"
            step="0.01"
            required
          />
          <input
            type="number"
            name="stock"
            value={newProduct.stock}
            onChange={handleInputChange}
            placeholder="Stock"
            required
          />
          <button type="submit">Add Product</button>
          <button type="button" onClick={exportUIData} style={{ marginLeft: '10px' }}>Export UI Data</button>
        </form>
      </div>
    </div>
  );
}

export default Inventory;
