import React, { useEffect, useState } from 'react';
import api from './api';
import Dashboard from './components/Dashboard';
import Navbar from './components/Navbar';
import Reports from './components/Reports';
import Sales from './components/Sales';
import Inventory from './components/Inventory';
import Footer from './components/Footer'; // Import the Footer component
import logo from './assets/logo.png';

function App() {
  const [products, setProducts] = useState([]);
  const [activeSection, setActiveSection] = useState('dashboard');
  const [refreshSignal, setRefreshSignal] = useState(0); // State to trigger refreshes

  // ✅ Fetch products from API
  const fetchProducts = async () => {
    try {
      const res = await api.get('/products');
      setProducts(res.data || []);
    } catch (error) {
      console.error('Failed to fetch products:', error);
    }
  };

  // ✅ Initial fetch
  useEffect(() => {
    fetchProducts();
  }, []);

  // ✅ Fetch products again whenever refreshSignal changes
  useEffect(() => {
    fetchProducts();
  }, [refreshSignal]);

  // ✅ Update product quantity locally
  const updateProductQuantity = (productId, newQuantity) => {
    setProducts(prev =>
      prev.map(p => (p.id === productId ? { ...p, quantity: newQuantity } : p))
    );
  };

  // ✅ Trigger refresh for Reports and Dashboard
  const handleRefresh = () => {
    setRefreshSignal(prev => prev + 1);
  };

  return (
    <div className="app" style={{ minHeight: '100vh', position: 'relative', paddingBottom: '60px' }}>
      <Navbar logo={logo} setActiveSection={setActiveSection} />
      <div className="main-content">
        <div className="content">
          {activeSection === 'dashboard' && (
            <Dashboard
              products={products}
              refreshSignal={refreshSignal} // ✅ Pass refresh signal
            />
          )}
          {activeSection === 'inventory' && (
            <Inventory
              products={products}
              onChanged={handleRefresh}
            />
          )}
          {activeSection === 'reports' && (
            <Reports
              refreshSignal={refreshSignal} // ✅ Pass refresh signal
            />
          )}
          {activeSection === 'sales' && (
            <Sales
              products={products}
              updateProductQuantity={updateProductQuantity}
              onSold={handleRefresh} // ✅ Trigger refresh after sale
            />
          )}
        </div>
      </div>
      <Footer /> {/* Add Footer component here */}
    </div>
  );
}

export default App;
