import React, { useState, useEffect } from 'react';
import api from '../api'; // Assuming this exists and handles API calls

export default function Dashboard({ products: initialProducts = [] }) {
  // Limit to first 5 products only
  const products = initialProducts.slice(0, 5);

  const totalItems = products.length;
  const totalStock = products.reduce((s, p) => s + Number(p.quantity || 0), 0);
  const lowStock = products.filter(p => Number(p.quantity || 0) <= 5).length;

  const handleImageError = (e) => {
    e.target.src = 'https://via.placeholder.com/150?text=Image+Not+Available';
  };

  return (
    <div className="dashboard card">
      <h3>Dashboard Overview</h3>
      <div className="summary-cards" style={{ display: 'flex', gap: '1rem' }}>
        <div className="summary-card" style={{ flex: 1 }}>
          <h4>Total Items</h4>
          <p><strong>{totalItems}</strong></p>
        </div>
        <div className="summary-card" style={{ flex: 1 }}>
          <h4>Total Stock</h4>
          <p><strong>{totalStock}</strong></p>
        </div>
        <div className="summary-card" style={{ flex: 1 }}>
          <h4>Low Stock</h4>
          <p><strong>{lowStock}</strong></p>
        </div>
      </div>

      <div className="product-cards" style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem', marginTop: '1rem' }}>
        {products.map(p => (
          <div key={p.id} className="product-card" style={{ border: '1px solid #ddd', padding: 10, width: 200 }}>
            <img
              src={p.image || 'https://via.placeholder.com/150?text=No+Image'}
              alt={p.name}
              className="product-image"
              style={{ width: '100%', height: 150, objectFit: 'cover' }}
              onError={handleImageError}
            />
            <div className="product-info" style={{ marginTop: 10 }}>
              <h4>{p.name}</h4>

              <div>
                <label>
                  Quantity:&nbsp;
                  <input
                    type="text"
                    value={p.quantity}
                    readOnly
                    style={{ width: '60px', backgroundColor: '#f5f5f5', border: '1px solid #ccc', cursor: 'default' }}
                  />
                </label>
                
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
