import React, { useEffect, useState } from 'react';
import api from '../api';

function Reports({ refreshSignal, transactions = [] }) {
  const [loading, setLoading] = useState(true);
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log('Refresh signal changed:', refreshSignal); // Debug log
    console.log('Transactions prop received:', transactions); // Debug transactions prop
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        console.log('Attempting to fetch products...'); // Debug start
        const prodRes = await api.get('/products', { timeout: 5000 }); // 5-second timeout
        if (!Array.isArray(prodRes.data)) {
          throw new Error('Products data is not an array');
        }
        setProducts(prodRes.data || []);
        console.log('Products fetched:', prodRes.data); // Debug success
      } catch (error) {
        console.error('Error fetching products for reports:', error);
        setError(`Failed to fetch product data: ${error.message || error}`);
      } finally {
        setLoading(false);
        console.log('Loading state set to false'); // Debug finally block
      }
    };

    fetchData();
  }, [refreshSignal]); // eslint-disable-line react-hooks/exhaustive-deps

  const today = new Date().toISOString().split('T')[0];

  const totalSales = (transactions || []).reduce((acc, txn) => {
    const product = products.find(p => p.id === txn?.productId);
    return acc + (txn?.amount || 0) * (product?.price || 0);
  }, 0).toFixed(2);

  const totalProducts = products.length;

  // Count of products needing restock (availableQuantity <= 5)
  const restockCount = products.filter(p => Number(p.quantity || 0) <= 5).length;

  const productsWithDetails = products.map(product => {
    console.log(`Calculating for product ${product.id || 'unknown'}:`, product); // Debug product
    const soldQuantity = (transactions || [])
      .filter(t => t?.productId === product.id && t?.type === 'deduct' && t?.note === 'sold')
      .reduce((acc, txn) => {
        console.log(`Transaction for product ${product.id}:`, txn); // Debug each transaction
        return acc + (txn?.amount || 0);
      }, 0);

    const availableQuantity = Number(product.quantity || 0); // ✅ Updated to use `quantity`

    return {
      name: product.name || 'Unnamed',
      price: Number(product.price || 0).toFixed(2),
      soldQuantity,
      availableQuantity,
    };
  });

  if (loading) return <div className="dashboard">Loading reports...</div>;
  if (error) return <div className="dashboard">Error: {error}</div>;

  return (
    <div className="dashboard">
      <div className="summary-cards" style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
        <div className="summary-card total-sales" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Total Sales</h4>
          <p>M{totalSales}</p>
        </div>
        <div className="summary-card total-products" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Total Products</h4>
          <p>{totalProducts}</p>
        </div>
        <div className="summary-card restock" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Restock</h4>
          <p>{restockCount} product{restockCount !== 1 ? 's' : ''} need{restockCount !== 1 ? '' : 's'} restocking</p>
        </div>
      </div>
      <div className="card" style={{ padding: '1rem', border: '1px solid #ccc', marginBottom: '1rem' }}>
        <h3>Product Overview</h3>
        {productsWithDetails.length === 0 ? (
          <p>No products available.</p>
        ) : (
          <table className="table" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Product Name</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Price (M)</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Sold Quantity</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Available Quantity</th>
              </tr>
            </thead>
            <tbody>
              {productsWithDetails.map((item, index) => (
                <tr key={index}>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.name}</td>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.price}</td>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.soldQuantity}</td>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.availableQuantity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Reports;
