import React, { useEffect, useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import api from './api';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import AddProductForm from './components/AddProductForm';
import ProductList from './components/ProductList';
import Dashboard from './components/Dashboard';
import StockTransactions from './components/StockTransactions';
import './styles.css';

function App() {
  const [products, setProducts] = useState([]);
  const [refreshKey, setRefreshKey] = useState(0);

  const fetchProducts = async () => {
    const res = await api.get('/products');
    setProducts(res.data);
  };

  const handleRefresh = () => {
    setRefreshKey(prev => prev + 1); // Trigger re-fetch for all components
    fetchProducts(); // Update products state
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div className="app-container">
      <Navbar />
      <div className="main-layout">
        <Sidebar />
        <div className="content">
          <Routes>
            <Route path="/dashboard" element={<Dashboard products={products} />} />
            <Route path="/products" element={
              <div className="grid">
                <div><AddProductForm onAdded={handleRefresh} /></div>
                <div><ProductList products={products} onChanged={handleRefresh} /></div>
              </div>
            } />
            <Route path="/transactions" element={<StockTransactions onRefresh={refreshKey} />} />
          </Routes>
        </div>
      </div>
    </div>
  );
}

export default App;
