import React, { useState, useEffect } from 'react';
import api from '../api';

function Inventory({ products = [], updateProductQuantityOrPrice, onChanged }) {
  const [loading, setLoading] = useState(true);
  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    category: '',
    price: '',
    quantity: '',
  });
  const [editedFields, setEditedFields] = useState({}); // { productId: { price?, quantity? } }

  useEffect(() => {
    setLoading(false); // since parent passed products
  }, [products]);

  const handleNewProductChange = (e) => {
    const { name, value } = e.target;
    setNewProduct(prev => ({ ...prev, [name]: value }));
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const { name, description, category, price, quantity } = newProduct;
    if (!name || !price || !quantity) {
      return alert('Please fill all required fields');
    }
    try {
      const newProductData = {
        name,
        description,
        category,
        price: parseFloat(price),
        quantity: parseInt(quantity, 10),
      };
      const res = await api.post('/products', newProductData);
      // update parent state
      if (res.data) {
        updateProductQuantityOrPrice(res.data.id, { quantity: res.data.quantity, price: res.data.price });
      }
      setNewProduct({ name: '', description: '', category: '', price: '', quantity: '' });
      if (onChanged) onChanged();
    } catch (error) {
      console.error('Failed to add product:', error);
      alert('Failed to add product');
    }
  };

  const handleFieldChange = (id, value, field) => {
    if (value === '' || /^\d*\.?\d*$/.test(value)) { // allow digits
      setEditedFields(prev => ({
        ...prev,
        [id]: { ...prev[id], [field]: value },
      }));
    }
  };

  const handleSave = async (product) => {
    const edits = editedFields[product.id] || {};
    const updatedQuantity = edits.quantity !== undefined ? parseInt(edits.quantity, 10) : product.quantity;
    const updatedPrice = edits.price !== undefined ? parseFloat(edits.price) : product.price;

    if (
      (edits.quantity !== undefined && (isNaN(updatedQuantity) || updatedQuantity < 0)) ||
      (edits.price !== undefined && (isNaN(updatedPrice) || updatedPrice < 0))
    ) {
      return alert('Enter valid quantity and price values.');
    }

    const isQuantityChanged = updatedQuantity !== product.quantity;
    const isPriceChanged = updatedPrice !== product.price;
    if (!isQuantityChanged && !isPriceChanged) {
      return;
    }

    try {
      const updatedProduct = { ...product, quantity: updatedQuantity, price: updatedPrice };
      await api.put(`/products/${product.id}`, updatedProduct);
      updateProductQuantityOrPrice(product.id, { quantity: updatedQuantity, price: updatedPrice });
      setEditedFields(prev => {
        const newData = { ...prev };
        delete newData[product.id];
        return newData;
      });
      if (onChanged) onChanged();
    } catch (error) {
      console.error('Error updating product:', error);
      alert('Failed to update product.');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;
    try {
      await api.delete(`/products/${id}`);
      updateProductQuantityOrPrice(id, { quantity: 0, price: 0 }); 
      if (onChanged) onChanged();
    } catch (error) {
      console.error('Failed to delete product:', error);
      alert('Failed to delete product.');
    }
  };

  if (loading) return <div className="dashboard">Loading inventory...</div>;

  return (
    <div className="dashboard">
      <div className="card inventory-table">
        <h3>Product Inventory</h3>
        <table className="table product-inventory" style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Category</th>
              <th>Price (M)</th>
              <th>Quantity</th>
              <th>Action</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {products.map(product => {
              const edits = editedFields[product.id] || {};
              const editedPrice = edits.price !== undefined ? edits.price : product.price;
              const editedQuantity = edits.quantity !== undefined ? edits.quantity : product.quantity;
              const isChanged = (editedPrice !== product.price) || (editedQuantity !== product.quantity);
              const isLow = Number(editedQuantity || 0) < 10;

              return (
                <tr key={product.id} style={{ backgroundColor: isLow ? '#ffcccc' : 'transparent' }}>
                  <td>{product.name}</td>
                  <td>{product.description || 'N/A'}</td>
                  <td>{product.category || 'N/A'}</td>
                  <td>
                    <input
                      type="text"
                      value={editedPrice}
                      onChange={e => handleFieldChange(product.id, e.target.value, 'price')}
                      style={{ width: '60px' }}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={editedQuantity}
                      onChange={e => handleFieldChange(product.id, e.target.value, 'quantity')}
                      style={{ width: '60px' }}
                    />
                  </td>
                  <td>
                    <button disabled={!isChanged} onClick={() => handleSave(product)}>
                      Save
                    </button>
                  </td>
                  <td>
                    <button
                      onClick={() => handleDelete(product.id)}
                      style={{ backgroundColor: '#e74c3c', color: 'white' }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      <div className="card inventory-form" style={{ marginTop: '20px' }}>
        <h3>Add New Product</h3>
        <form onSubmit={handleAddProduct} className="add-product-form">
          <input
            name="name"
            value={newProduct.name}
            onChange={handleNewProductChange}
            placeholder="Product Name"
            required
          />
          <input
            name="description"
            value={newProduct.description}
            onChange={handleNewProductChange}
            placeholder="Description"
          />
          <input
            name="category"
            value={newProduct.category}
            onChange={handleNewProductChange}
            placeholder="Category"
          />
          <input
            name="price"
            type="number"
            step="0.01"
            min="0"
            value={newProduct.price}
            onChange={handleNewProductChange}
            placeholder="Price"
            required
          />
          <input
            name="quantity"
            type="number"
            min="0"
            value={newProduct.quantity}
            onChange={handleNewProductChange}
            placeholder="Quantity"
            required
          />
          <button type="submit">Add Product</button>
        </form>
      </div>
    </div>
  );
}

export default Inventory;
