import React, { useState } from 'react';
import api from '../api';

function Inventory({ products = [], onChanged }) {
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({ name: '', price: '', stock: '' });
  const [loading, setLoading] = useState(false);

  // Handle update existing product
  const updateProduct = async () => {
    if (!editingProduct.name.trim()) return alert('Product name required');
    if (editingProduct.price < 0 || editingProduct.stock < 0) return alert('Price and stock must be non-negative');

    setLoading(true);
    try {
      await api.put(`/products/${editingProduct.id}`, {
        name: editingProduct.name,
        price: parseFloat(editingProduct.price),
        stock: parseInt(editingProduct.stock, 10),
      });
      setEditingProduct(null);
      if (onChanged) onChanged();
    } catch (err) {
      console.error(err);
      alert('Failed to update product');
    }
    setLoading(false);
  };

  // Handle add new product
  const addProduct = async () => {
    if (!newProduct.name.trim()) return alert('Product name required');
    if (newProduct.price < 0 || newProduct.stock < 0) return alert('Price and stock must be non-negative');

    setLoading(true);
    try {
      await api.post('/products', {
        name: newProduct.name,
        price: parseFloat(newProduct.price),
        stock: parseInt(newProduct.stock, 10),
      });
      setNewProduct({ name: '', price: '', stock: '' });
      if (onChanged) onChanged();
    } catch (err) {
      console.error(err);
      alert('Failed to add product');
    }
    setLoading(false);
  };

  return (
    <div className="dashboard">
      <h3>Inventory Management</h3>

      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '15px' }}>
        {/* Existing products */}
        {products.map(p => (
          <div key={p.id} className="card" style={{ width: 250, padding: 15 }}>
            {editingProduct?.id === p.id ? (
              <>
                <input
                  type="text"
                  value={editingProduct.name}
                  onChange={e => setEditingProduct({ ...editingProduct, name: e.target.value })}
                  placeholder="Product Name"
                  style={{ width: '100%', marginBottom: 8 }}
                />
                <input
                  type="number"
                  min="0"
                  step="0.01"
                  value={editingProduct.price}
                  onChange={e => setEditingProduct({ ...editingProduct, price: e.target.value })}
                  placeholder="Price"
                  style={{ width: '100%', marginBottom: 8 }}
                />
                <input
                  type="number"
                  min="0"
                  value={editingProduct.stock}
                  onChange={e => setEditingProduct({ ...editingProduct, stock: e.target.value })}
                  placeholder="Stock"
                  style={{ width: '100%', marginBottom: 8 }}
                />
                <button onClick={updateProduct} disabled={loading} style={{ marginRight: 8 }}>
                  Save
                </button>
                <button onClick={() => setEditingProduct(null)} disabled={loading}>
                  Cancel
                </button>
              </>
            ) : (
              <>
                <h4>{p.name}</h4>
                <p>Price: M{p.price.toFixed(2)}</p>
                <p>Stock: {p.stock}</p>
                <button onClick={() => setEditingProduct({ ...p })}>Edit</button>
              </>
            )}
          </div>
        ))}

        {/* Add new product */}
        <div className="card" style={{ width: 250, padding: 15, backgroundColor: '#f0f0f0' }}>
          <h4>Add New Product</h4>
          <input
            type="text"
            value={newProduct.name}
            onChange={e => setNewProduct({ ...newProduct, name: e.target.value })}
            placeholder="Product Name"
            style={{ width: '100%', marginBottom: 8 }}
          />
          <input
            type="number"
            min="0"
            step="0.01"
            value={newProduct.price}
            onChange={e => setNewProduct({ ...newProduct, price: e.target.value })}
            placeholder="Price"
            style={{ width: '100%', marginBottom: 8 }}
          />
          <input
            type="number"
            min="0"
            value={newProduct.stock}
            onChange={e => setNewProduct({ ...newProduct, stock: e.target.value })}
            placeholder="Stock"
            style={{ width: '100%', marginBottom: 8 }}
          />
          <button onClick={addProduct} disabled={loading}>
            Add Product
          </button>
        </div>
      </div>
    </div>
  );
}

export default Inventory;
