import React from 'react';

export default function Dashboard({ products = [] }) {
  const totalItems = products.length;
  const totalStock = products.reduce((s, p) => s + Number(p.quantity || 0), 0);
  const lowStock = products.filter(p => Number(p.quantity || 0) <= 5).length;

  return (
    <div className="dashboard-container">
      <style>
        {`
          .dashboard-container {
            max-width: 500px;
            margin: 40px auto;
            padding: 20px;
            background: linear-gradient(135deg, #e0f7fa, #ffffff);
            box-shadow: 0 8px 20px rgba(0, 0, 0, 0.1);
            border-radius: 12px;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            color: #333;
          }

          .stat {
            font-size: 18px;
            margin: 15px 0;
            padding: 12px;
            background-color: #f9f9f9;
            border-left: 5px solid #00796b;
            border-radius: 6px;
            transition: transform 0.3s ease;
          }

          .stat:hover {
            transform: scale(1.03);
            background-color: #e0f2f1;
          }

          .stat strong {
            color: #004d40;
          }

          .stat:nth-child(3) {
            border-left-color: #f44336;
          }

          .stat:nth-child(3) strong {
            color: #c62828;
          }
        `}
      </style>

      <div className="stat">Total products: <strong>{totalItems}</strong></div>
      <div className="stat">Total stock units: <strong>{totalStock}</strong></div>
      <div className="stat">Low stock alerts: <strong>{lowStock}</strong></div>
    </div>
  );
}
