import React, { useState, useEffect } from 'react';
import api from '../api'; // Your API handler

function Reports({ products }) {
  const [salesData, setSalesData] = useState([]);
  const [stockMovements, setStockMovements] = useState([]);
  const [alerts, setAlerts] = useState({ lowStock: [], outOfStock: [] });
  const [financials, setFinancials] = useState({ totalRevenue: 0, totalProfit: 0 });

  useEffect(() => {
    async function fetchReports() {
      try {
        // Fetch sales transactions
        const salesRes = await api.get('/sales');
        const sales = salesRes.data || [];

        // Fetch stock movement transactions (stock in/out history)
        const stockRes = await api.get('/stock-movements');
        const movements = stockRes.data || [];

        // Calculate alerts based on current products
        const lowStock = products.filter(p => Number(p.stock || 0) <= 5 && Number(p.stock || 0) > 0);
        const outOfStock = products.filter(p => Number(p.stock || 0) === 0);

        // Calculate financials from sales data
        let totalRevenue = 0;
        let totalProfit = 0;
        sales.forEach(sale => {
          const quantity = Number(sale.quantity || 0);
          const price = Number(sale.price || 0);
          const costPrice = Number(sale.costPrice || 0);
          totalRevenue += price * quantity;
          totalProfit += (price - costPrice) * quantity;
        });

        setSalesData(sales);
        setStockMovements(movements);
        setAlerts({ lowStock, outOfStock });
        setFinancials({ totalRevenue, totalProfit });
      } catch (err) {
        console.error('Error fetching reports data:', err);
      }
    }
    fetchReports();
  }, [products]);

  return (
    <div className="reports-container" style={{ maxWidth: '1200px', margin: '0 auto', padding: 20 }}>
      <h3 style={{ textAlign: 'center', marginBottom: 20 }}>Reports</h3>

      {/* Financial Insights */}
      <section className="financial-insights" style={{ marginBottom: 30 }}>
        <h4>Financial Insights</h4>
        <p><strong>Total Revenue:</strong> M{financials.totalRevenue.toFixed(2)}</p>
        <p><strong>Total Profit:</strong> M{financials.totalProfit.toFixed(2)}</p>
      </section>

      {/* Sales Reports */}
      <section className="sales-reports" style={{ marginBottom: 30 }}>
        <h4>Sales Reports</h4>
        {salesData.length === 0 ? (
          <p>No sales data available.</p>
        ) : (
          <>
            <p><strong>Total Sales Records:</strong> {salesData.length}</p>
            <h5>Top Selling Products</h5>
            <ul>
              {salesData
                .sort((a, b) => (b.quantity || 0) - (a.quantity || 0))
                .slice(0, 5)
                .map(sale => (
                  <li key={sale.id}>
                    {sale.productName} — {sale.quantity} units sold
                  </li>
                ))}
            </ul>
          </>
        )}
      </section>

      {/* Stock Movements / Inventory History */}
      <section className="stock-movements" style={{ marginBottom: 30 }}>
        <h4>Stock Movements</h4>
        {stockMovements.length === 0 ? (
          <p>No stock movement records available.</p>
        ) : (
          <table
            style={{
              width: '100%',
              borderCollapse: 'collapse',
              marginTop: 10,
              fontSize: 14,
            }}
          >
            <thead>
              <tr>
                <th style={{ borderBottom: '1px solid #ccc', padding: 8 }}>Date</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: 8 }}>Product</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: 8 }}>Type</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: 8 }}>Quantity</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: 8 }}>Note</th>
              </tr>
            </thead>
            <tbody>
              {stockMovements.map(movement => (
                <tr key={movement.id}>
                  <td style={{ borderBottom: '1px solid #eee', padding: 8 }}>{movement.date}</td>
                  <td style={{ borderBottom: '1px solid #eee', padding: 8 }}>{movement.productName}</td>
                  <td style={{ borderBottom: '1px solid #eee', padding: 8 }}>{movement.type}</td>
                  <td style={{ borderBottom: '1px solid #eee', padding: 8 }}>{movement.quantity}</td>
                  <td style={{ borderBottom: '1px solid #eee', padding: 8 }}>{movement.note}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>

      {/* Alerts */}
      <section className="alerts" style={{ marginBottom: 30 }}>
        <h4>Stock Alerts</h4>
        <div style={{ marginBottom: 10 }}>
          <strong>Low Stock ({alerts.lowStock.length} items):</strong>
          {alerts.lowStock.length === 0 ? (
            <p>None</p>
          ) : (
            <ul>
              {alerts.lowStock.map(p => (
                <li key={p.id}>
                  {p.name} ({p.stock} left)
                </li>
              ))}
            </ul>
          )}
        </div>
        <div>
          <strong>Out of Stock ({alerts.outOfStock.length} items):</strong>
          {alerts.outOfStock.length === 0 ? (
            <p>None</p>
          ) : (
            <ul>
              {alerts.outOfStock.map(p => (
                <li key={p.id}>{p.name}</li>
              ))}
            </ul>
          )}
        </div>
      </section>
    </div>
  );
}

export default Reports;
