import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3001', // Ensure this matches your backend server URL
  timeout: 15000, // Increased timeout to 15 seconds for slower connections
  headers: {
    'Content-Type': 'application/json',
  },
});

// Response interceptor for detailed error handling
api.interceptors.response.use(
  response => response,
  error => {
    console.error('API Error:', {
      url: error.config?.url,
      method: error.config?.method,
      data: error.config?.data,
      message: error.message,
      status: error.response?.status,
      responseData: error.response?.data,
    });
    if (error.code === 'ECONNABORTED') {
      return Promise.reject(new Error('Request timed out. Please check if the server is running at http://localhost:3001.'));
    }
    if (!error.response) {
      return Promise.reject(new Error('Network error. Please check your internet connection or server status at http://localhost:3001.'));
    }
    return Promise.reject(error);
  }
);

export default api;
