import React, { useState, useEffect, useCallback } from 'react';
import api from '../api';

function Sales() {
  const [products, setProducts] = useState([]);
  const [salesData, setSalesData] = useState([]);
  const [filterDate, setFilterDate] = useState('');
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const [receipt, setReceipt] = useState(null);

  // Fetch products
  const fetchProducts = useCallback(async () => {
    try {
      const res = await api.get('/products');
      setProducts(res.data || []);
    } catch (err) {
      console.error('Failed to fetch products:', err);
    }
  }, []);

  // Fetch sales
  const fetchSales = useCallback(async () => {
    try {
      const url = filterDate ? `/sales?date=${filterDate}` : '/sales';
      const res = await api.get(url);
      setSalesData(res.data || []);
    } catch (err) {
      console.error('Failed to fetch sales:', err);
    }
  }, [filterDate]);

  // Load data
  useEffect(() => {
    setLoading(true);
    Promise.all([fetchProducts(), fetchSales()]).finally(() => setLoading(false));
  }, [fetchProducts, fetchSales]);

  // Build summary with correct reduce logic
  const dailySummary = products.map(p => {
    const sold = salesData
      .filter(s => s.productId === p.id)
      .reduce((sum, s) => sum + (s.quantity || 0), 0); // Ensure numeric

    const stockLeft = p.stock - sold;
    return { ...p, sold, stockLeft, revenue: sold * p.price };
  });

  const totalRevenue = dailySummary.reduce((sum, p) => sum + (p.revenue || 0), 0);
  const totalStockLeft = dailySummary.reduce((sum, p) => sum + (p.stockLeft || 0), 0);

  const availableStock = selectedProduct
    ? products.find(p => p.id === selectedProduct)?.stock || 0
    : 0;

  // Handle form submission
  const sellProduct = async e => {
    e.preventDefault();
    if (!selectedProduct || quantity < 1 || quantity > availableStock) {
      return alert('Invalid quantity or product.');
    }

    const p = products.find(p => p.id === selectedProduct);
    const sale = {
      productId: selectedProduct,
      name: p.name,
      quantity,
      amount: p.price * quantity,
      date: new Date().toISOString().split('T')[0],
    };

    try {
      await api.post('/sales', sale);
      await fetchProducts();
      await fetchSales();
      setReceipt(sale);
      setSelectedProduct('');
      setQuantity(1);
    } catch (err) {
      console.error(err);
      alert('Sale failed.');
    }
  };

  if (loading) return <div className="dashboard">Loading data...</div>;

  return (
    <div className="dashboard">
      <div className="card">
        <h3>Sales Overview</h3>
        <label>
          Filter by Date:
          <input type="date" value={filterDate} onChange={e => setFilterDate(e.target.value)} />
        </label>
        <table className="table" style={{ marginTop: 10 }}>
          <thead>
            <tr>
              <th>Product</th>
              <th>Initial Stock</th>
              <th>Sold</th>
              <th>Stock Left</th>
              <th>Revenue</th>
            </tr>
          </thead>
          <tbody>
            {dailySummary.map(p => (
              <tr key={p.id} className={p.stockLeft < 10 ? 'low' : ''}>
                <td>{p.name}</td>
                <td>{p.stock}</td>
                <td>{p.sold}</td>
                <td>{p.stockLeft}</td>
                <td>{`M${p.revenue.toFixed(2)}`}</td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <td><strong>Totals</strong></td>
              <td>-</td>
              <td>-</td>
              <td><strong>{totalStockLeft}</strong></td>
              <td><strong>{`M${totalRevenue.toFixed(2)}`}</strong></td>
            </tr>
          </tfoot>
        </table>
      </div>

      <div className="card" style={{ marginTop: 20 }}>
        <h3>Sell Product</h3>
        <form onSubmit={sellProduct}>
          <select
            value={selectedProduct}
            onChange={e => { setSelectedProduct(e.target.value); setQuantity(1); }}
            required
          >
            <option value="">Select Product</option>
            {products.map(p => (
              <option key={p.id} value={p.id} disabled={p.stock === 0}>
                {p.name} — Stock: {p.stock}
              </option>
            ))}
          </select>

          <input
            type="number"
            min="1"
            max={availableStock}
            value={quantity}
            onChange={e => {
              const val = Math.min(Math.max(1, parseInt(e.target.value, 10) || 1), availableStock);
              setQuantity(val);
            }}
            required
            style={{ marginLeft: 10, width: 80 }}
          />

          <button type="submit" disabled={availableStock === 0} style={{ marginLeft: 10 }}>
            Sell
          </button>
          {selectedProduct && <span style={{ marginLeft: 10 }}>Available: {availableStock}</span>}
        </form>
      </div>

      {receipt && (
        <div className="card receipt" style={{ margin: '20px 0', padding: 15, border: '1px solid #ddd', maxWidth: 400 }}>
          <h4>Receipt</h4>
          <p><strong>Date:</strong> {new Date(receipt.date).toLocaleDateString()}</p>
          <p><strong>Product:</strong> {receipt.name}</p>
          <p><strong>Quantity:</strong> {receipt.quantity}</p>
          <p><strong>Total:</strong> {`M${receipt.amount.toFixed(2)}`}</p>
          <hr />
          <p>Thank you!</p>
          <button onClick={() => setReceipt(null)} style={{ marginTop: 10 }}>Close</button>
        </div>
      )}
    </div>
  );
}

export default Sales;
