import React, { useState } from 'react';
import api from '../api';

function Sales({ products, updateProductQuantity, onRefresh }) {
  const [loadingIds, setLoadingIds] = useState([]);

  const sellProduct = async (productId) => {
    try {
      setLoadingIds((ids) => [...ids, productId]);

      // Call backend transaction to deduct 1 from quantity
      const res = await api.post(`/products/${productId}/transaction`, {
        type: 'deduct',
        amount: 1,
        note: 'sold',
      });

      // Update quantity in parent state
      updateProductQuantity(productId, res.data.product.quantity);

      // Trigger reports refresh
      if (onRefresh) onRefresh();
    } catch (err) {
      console.error('Sale failed:', err);
      alert('Sale failed, check console.');
    } finally {
      setLoadingIds((ids) => ids.filter((id) => id !== productId));
    }
  };

  return (
    <div>
      <h2>Sales</h2>
      {products.length === 0 && <p>No products available.</p>}
      <ul>
        {products.map((product) => (
          <li key={product.id} style={{ marginBottom: 15 }}>
            <strong>{product.name}</strong> - Quantity: {product.quantity}
            <button
              onClick={() => sellProduct(product.id)}
              disabled={loadingIds.includes(product.id) || product.quantity <= 0}
              style={{ marginLeft: 10 }}
            >
              {loadingIds.includes(product.id) ? 'Processing...' : 'Sell 1'}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Sales;
