import React, { useState } from 'react';
import api from '../api';

function Sales({ products = [], updateProductQuantityOrPrice, onSold }) {
  const [selling, setSelling] = useState({});
  const [isSubmitting, setIsSubmitting] = useState({}); // Track submission state per product

  const handleQuantityChange = (productId, value) => {
    const product = products.find(p => p.id === productId);
    if (!product) return;
    const quantityAllowed = Number(product.quantity || 0);
    const num = Math.max(1, Math.min(parseInt(value, 10) || 1, quantityAllowed));
    setSelling(prev => ({ ...prev, [productId]: num }));
  };

  // Retry mechanism for API calls
  const retryApiCall = async (fn, retries = 3, delay = 1000) => {
    for (let attempt = 1; attempt <= retries; attempt++) {
      try {
        return await fn();
      } catch (err) {
        if (attempt === retries) throw err;
        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }
  };

  const sellProduct = async (productId) => {
    const qtyToSell = selling[productId] || 1;
    const product = products.find(p => p.id === productId);
    if (!product) return alert('Product not found');

    if (qtyToSell > product.quantity) {
      return alert(`Quantity (${qtyToSell}) exceeds available quantity (${product.quantity}).`);
    }

    setIsSubmitting(prev => ({ ...prev, [productId]: true })); // Disable button during submission

    try {
      // Update product quantity directly with retry mechanism
      const updatedQuantity = product.quantity - qtyToSell;
      const updatedProduct = { ...product, quantity: updatedQuantity };

      await retryApiCall(() => api.put(`/products/${productId}`, updatedProduct));

      updateProductQuantityOrPrice(productId, { quantity: updatedQuantity });
      if (onSold) onSold();
      setSelling(prev => ({ ...prev, [productId]: 1 }));
      alert(`Sold ${qtyToSell} x ${product.name} successfully! Total: M${(product.price * qtyToSell).toFixed(2)}`);
    } catch (err) {
      console.error('Sale failed:', err);
      if (err.message.includes('timed out')) {
        alert('Request timed out. Please ensure the server is running and try again.');
      } else if (err.response) {
        switch (err.response.status) {
          case 400:
            alert('Invalid sale data. Please check the quantity.');
            break;
          case 500:
            alert('Server error. Please try again later.');
            break;
          default:
            alert(`Sale failed: ${err.response.data?.message || 'Unknown error'}`);
        }
      } else {
        alert('Network error. Please check your internet connection or server status.');
      }
    } finally {
      setIsSubmitting(prev => ({ ...prev, [productId]: false })); // Re-enable button
    }
  };

  if (products.length === 0) return <div>No products available</div>;

  return (
    <div className="dashboard">
      <h3>Sales</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 15 }}>
        {products.map(product => (
          <div
            key={product.id}
            className="card"
            style={{ width: 250, padding: 15, border: '1px solid #ccc', borderRadius: 8 }}
          >
            <h4>{product.name}</h4>
            <p>Price: M{Number(product.price || 0).toFixed(2)}</p>
            <p>Quantity: {product.quantity || 0}</p>
            <input
              type="number"
              min="1"
              max={product.quantity || 0}
              value={selling[product.id] || 1}
              onChange={e => handleQuantityChange(product.id, e.target.value)}
              disabled={product.quantity === 0 || isSubmitting[product.id]}
              style={{ width: '80px', marginBottom: 8 }}
            />
            <button
              onClick={() => sellProduct(product.id)}
              disabled={product.quantity === 0 || isSubmitting[product.id]}
              style={{
                width: '100%',
                backgroundColor: product.quantity === 0 || isSubmitting[product.id] ? '#ccc' : '#007bff',
                color: '#fff',
                border: 'none',
                padding: '8px',
                cursor: product.quantity === 0 || isSubmitting[product.id] ? 'not-allowed' : 'pointer',
              }}
            >
              {isSubmitting[product.id] ? 'Processing...' : product.quantity === 0 ? 'Out of Stock' : 'Sell'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Sales;
