import React, { useEffect, useState } from 'react';
import api from '../api';

function Sales({ products: initialProducts, onChanged }) {
  const [salesData, setSalesData] = useState([]);
  const [totalSales, setTotalSales] = useState(0);
  const [filterDate, setFilterDate] = useState(new Date().toISOString().split('T')[0]);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [products, setProducts] = useState(initialProducts || []);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setProducts(initialProducts || []);
    setLoading(false);
  }, [initialProducts]);

  const fetchData = async () => {
    try {
      const [productsRes, transactionsRes] = await Promise.all([
        api.get('/products'),
        api.get('/transactions')
      ]);
      const staticProducts = productsRes.data || [];
      const allTransactions = transactionsRes.data || [];

      setProducts(staticProducts);

      // Compute sales data for the selected date
      const dailyDeducts = allTransactions.filter(t => t.date.split('T')[0] === filterDate && t.type === 'deduct');
      const dailySales = {};
      dailyDeducts.forEach(t => {
        dailySales[t.productId] = (dailySales[t.productId] || 0) + t.amount;
      });

      let totalCash = 0;
      const summary = staticProducts.map(p => {
        const sold = dailySales[p.id] || 0;
        const initial = (Number(p.quantity) || 0) + sold;
        const left = Number(p.quantity) || 0;
        const cash = sold * (Number(p.price) || 0);
        totalCash += cash;
        return { name: p.name, initial, sold, left, cash: cash.toFixed(2) };
      });

      setSalesData(summary);
      setTotalSales(totalCash);
    } catch (err) {
      console.error('Error fetching data:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [filterDate, fetchData]);

  const sellProduct = async (e) => {
    e.preventDefault();
    if (!selectedProduct || quantity <= 0) {
      alert('Please select a product and enter a valid quantity.');
      return;
    }

    const product = products.find(p => p.id === selectedProduct);
    if (!product || (Number(product.quantity) || 0) < quantity) {
      alert('Insufficient stock or invalid product.');
      return;
    }

    if (!window.confirm(`Confirm sale of ${quantity} ${product.name} for $${(product.price * quantity).toFixed(2)}?`)) {
      return;
    }

    try {
      const transactionData = {
        type: 'deduct',
        amount: quantity,
        note: 'sold',
        productId: selectedProduct,
        date: new Date().toISOString()
      };
      await api.post(`/products/${selectedProduct}/transaction`, transactionData);

      // Update local products state
      const updatedProducts = products.map(p =>
        p.id === selectedProduct ? { ...p, quantity: (Number(p.quantity) || 0) - quantity } : p
      );
      setProducts(updatedProducts);

      // Refresh data
      await fetchData();
      if (onChanged) onChanged();

      setQuantity(1);
      setSelectedProduct('');
    } catch (err) {
      console.error('Error processing sale:', err);
      alert('Failed to record sale. Please try again.');
    }
  };

  const maxQuantity = selectedProduct ? (products.find(p => p.id === selectedProduct)?.quantity || 0) : 0;

  if (loading) return <div className="dashboard">Loading sales...</div>;

  return (
    <div className="dashboard">
      <div className="card">
        <h3>Sales Overview</h3>
        <div style={{ marginBottom: '15px' }}>
          <label>Filter by Date: </label>
          <input
            type="date"
            value={filterDate}
            onChange={(e) => setFilterDate(e.target.value)}
          />
        </div>
        <p>Total Sales: ${totalSales.toFixed(2)}</p>
        <p>Total Stock Left: {products.reduce((sum, p) => sum + (Number(p.quantity) || 0), 0)}</p>
      </div>
      <div className="card">
        <h3>Select Product to Sell</h3>
        <table className="table product-selection">
          <thead>
            <tr>
              <th>Name</th>
              <th>Stock</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {products.map(p => (
              <tr
                key={p.id}
                onClick={() => {
                  setSelectedProduct(p.id);
                  setQuantity(1); // Reset quantity when selecting a new product
                }}
                style={{
                  cursor: 'pointer',
                  backgroundColor: selectedProduct === p.id ? '#e0e0e0' : 'transparent',
                }}
              >
                <td>{p.name}</td>
                <td>{p.quantity || 0}</td>
                <td>${(Number(p.price) || 0).toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <form onSubmit={sellProduct} className="add-product-form" style={{ marginTop: '15px' }}>
          <input
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(Math.min(Math.max(1, parseInt(e.target.value) || 1), maxQuantity))}
            min="1"
            max={maxQuantity}
            placeholder="Quantity"
            required
            style={{ width: '100px', marginRight: '10px' }}
          />
          <button type="submit" disabled={!selectedProduct}>Sell</button>
        </form>
      </div>
      <div className="card">
        <h3>Daily Sales Summary</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Product</th>
              <th>Initial</th>
              <th>Sold</th>
              <th>Left</th>
              <th>Cash</th>
            </tr>
          </thead>
          <tbody>
            {salesData.map((item, index) => (
              <tr key={index}>
                <td>{item.name}</td>
                <td>{item.initial}</td>
                <td>{item.sold}</td>
                <td>{item.left}</td>
                <td>${item.cash}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Sales;
