import React, { useEffect, useState } from 'react';
import api from './api';
import Dashboard from './components/Dashboard';
import Navbar from './components/Navbar';
import Reports from './components/Reports';
import Sales from './components/Sales';
import Inventory from './components/Inventory';
import Footer from './components/Footer'; // Import the Footer component
import logo from './assets/logo.png';

function App() {
  const [products, setProducts] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [activeSection, setActiveSection] = useState('dashboard');
  const [refreshSignal, setRefreshSignal] = useState(0); // State to trigger refreshes

  const fetchData = async () => {
    try {
      const [productsRes, transactionsRes] = await Promise.all([
        api.get('/products'),
        api.get('/transactions'),
      ]);
      setProducts(productsRes.data || []);
      setTransactions(transactionsRes.data || []);
    } catch (error) {
      console.error('Failed to fetch data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Function to update product quantity after sale
  const updateProductQuantity = (productId, newQuantity) => {
    setProducts(prev =>
      prev.map(p => (p.id === productId ? { ...p, quantity: newQuantity } : p))
    );
    handleRefresh(); // Trigger refresh to sync with API
  };

  // Handler for refresh signal update
  const handleRefresh = () => {
    fetchData(); // Re-fetch data to ensure consistency
    setRefreshSignal(prev => prev + 1); // Increment to trigger useEffect in Reports
  };

  return (
    <div className="app" style={{ minHeight: '100vh', position: 'relative', paddingBottom: '60px' }}>
      <Navbar logo={logo} setActiveSection={setActiveSection} />
      <div className="main-content">
        <div className="content">
          {activeSection === 'dashboard' && <Dashboard products={products} />}
          {activeSection === 'inventory' && <Inventory products={products} onChanged={handleRefresh} />}
          {activeSection === 'reports' && <Reports refreshSignal={refreshSignal} transactions={transactions} />}
          {activeSection === 'sales' && (
            <Sales
              products={products}
              updateProductQuantity={updateProductQuantity}
              onSale={handleRefresh}
            />
          )}
        </div>
      </div>
      <Footer /> {/* Add Footer component here */}
    </div>
  );
}

export default App;
