import React, { useEffect, useState } from 'react';
import api from './api';
import Dashboard from './components/Dashboard';
import Navbar from './components/Navbar';
import Reports from './components/Reports';
import Sales from './components/Sales';
import Inventory from './components/Inventory';
import logo from './assets/logo.png';

function App() {
  const [mergedProducts, setMergedProducts] = useState([]);
  const [uiData, setUIData] = useState([]);
  const [activeSection, setActiveSection] = useState('dashboard');

  const fetchProducts = async () => {
    const res = await api.get('/products');
    const staticProducts = res.data || [];
    const savedUIData = localStorage.getItem('uiData') ? JSON.parse(localStorage.getItem('uiData')) : [];
    setUIData(savedUIData);

    const merged = [...staticProducts];
    savedUIData.forEach(uiProduct => {
      if (!merged.some(p => p.id === uiProduct.id || (p.name === uiProduct.name && p.price === uiProduct.price))) {
        merged.push({ ...uiProduct, id: uiProduct.id || Date.now() + Math.random().toString(36).substr(2, 9) });
      }
    });
    setMergedProducts(merged);
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div className="app">
      <Navbar logo={logo} setActiveSection={setActiveSection} />
      <div className="main-content">
        <div className="content">
          {activeSection === 'dashboard' && <Dashboard products={mergedProducts} />}
          {activeSection === 'inventory' && <Inventory products={mergedProducts} onChanged={fetchProducts} />}
          {activeSection === 'reports' && <Reports />}
          {activeSection === 'sales' && <Sales />}
        </div>
      </div>
    </div>
  );
}

export default App;
