import React, { useState, useEffect } from 'react';
import api from '../api';

function Sales({ products: initialProducts, updateProductQuantity }) {
  const [products, setProducts] = useState(initialProducts || []);
  const [selling, setSelling] = useState({});

  useEffect(() => {
    setProducts(initialProducts || []);
  }, [initialProducts]);

  const handleQuantityChange = (productId, value) => {
    const product = products.find(p => p.id === productId);
    if (!product) return;
    const stock = Number(product.quantity || 0);
    const num = Math.max(1, Math.min(parseInt(value, 10) || 1, stock));
    setSelling(prev => ({ ...prev, [productId]: num }));
  };

  const sellProduct = async (productId) => {
    const quantity = selling[productId] || 1;
    const product = products.find(p => p.id === productId);
    if (!product) return alert('Product not found');

    if (quantity > product.quantity) {
      return alert(`Quantity (${quantity}) exceeds available stock (${product.quantity}).`);
    }

    const transactionData = {
      productId,
      name: product.name,
      quantity,
      amount: product.price * quantity,
      date: new Date().toISOString().split('T')[0],
    };

    try {
      // Record transaction
      await api.post('/transactions', transactionData);

      // Update product quantity on the server
      const updatedProduct = { ...product, quantity: product.quantity - quantity };
      await api.put(`/products/${productId}`, updatedProduct);

      // Update local state
      setProducts(prevProducts =>
        prevProducts.map(p => (p.id === productId ? updatedProduct : p))
      );

      // Inform parent component (if provided)
      if (updateProductQuantity) {
        updateProductQuantity(productId, updatedProduct.quantity);
      }

      // Reset selling quantity for this product
      setSelling(prev => ({ ...prev, [productId]: 1 }));

      alert(`Sold ${quantity} x ${product.name} successfully! Total: M${(product.price * quantity).toFixed(2)}`);

    } catch (err) {
      console.error('Sale failed:', err);
      if (err.response) {
        switch (err.response.status) {
          case 400:
            alert('Invalid sale data. Please check the quantity.');
            break;
          case 500:
            alert('Server error. Please try again later.');
            break;
          default:
            alert('Sale failed. Check console for details.');
        }
      } else {
        alert('Network error. Please check your connection.');
      }
    }
  };

  if (!products.length) return <div>No products available</div>;

  return (
    <div className="dashboard">
      <h3>Sales</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 15 }}>
        {products.map(product => (
          <div
            key={product.id}
            className="card"
            style={{ width: 250, padding: 15, border: '1px solid #ccc', borderRadius: 8 }}
          >
            {product.image && (
              <img
                src={product.image}
                alt={product.name}
                style={{ width: '100%', height: 150, objectFit: 'cover', marginBottom: 10 }}
                onError={e => e.target.src = 'https://via.placeholder.com/150?text=No+Image'}
              />
            )}
            <h4>{product.name}</h4>
            <p>Price: M{Number(product.price || 0).toFixed(2)}</p>
            <p>Stock: {product.quantity || 0}</p>
            <input
              type="number"
              min="1"
              max={product.quantity || 0}
              value={selling[product.id] || 1}
              onChange={e => handleQuantityChange(product.id, e.target.value)}
              disabled={product.quantity === 0}
              style={{ width: '80px', marginBottom: 8 }}
            />
            <button
              onClick={() => sellProduct(product.id)}
              disabled={product.quantity === 0}
              style={{
                width: '100%',
                backgroundColor: product.quantity === 0 ? '#ccc' : '#007bff',
                color: '#fff',
                border: 'none',
                padding: '8px',
                cursor: product.quantity === 0 ? 'not-allowed' : 'pointer',
              }}
            >
              {product.quantity === 0 ? 'Out of Stock' : 'Sell'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Sales;
