import React, { useEffect, useState } from 'react';
import api from '../api';

function Reports({ refreshSignal }) {
  const [loading, setLoading] = useState(true);
  const [products, setProducts] = useState([]);
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [prodRes, transRes] = await Promise.all([
          api.get('/products'),
          api.get('/transactions'),
        ]);
        setProducts(prodRes.data || []);
        setTransactions(transRes.data || []);
      } catch (error) {
        console.error('Error fetching data for reports:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [refreshSignal]);

  // Current date for filtering today's transactions
  const today = new Date().toISOString().split('T')[0];

  // Calculate stock metrics and revenue
  const totalStock = products.reduce((acc, product) => acc + (product.quantity || 0), 0);
  const dailySales = transactions
    .filter(t => t.date === today && t.type === 'deduct' && t.note === 'sold')
    .reduce((acc, txn) => acc + (txn.amount || 0), 0);
  const stockLeft = Math.max(0, totalStock - dailySales);

  // Calculate total revenue: sum(price * amount) for all sales transactions
  const totalRevenue = transactions
    .filter(t => t.type === 'deduct' && t.note === 'sold')
    .reduce((acc, txn) => {
      const product = products.find(p => p.id === txn.productId);
      return acc + ((product?.price || 0) * (txn.amount || 0));
    }, 0)
    .toFixed(2);

  if (loading) return <div className="dashboard">Loading reports...</div>;

  return (
    <div className="dashboard">
      <div className="summary-cards" style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
        <div className="summary-card total-stock" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Total Stock</h4>
          <p>{totalStock}</p>
        </div>
        <div className="summary-card stock-left" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Stock Left Today</h4>
          <p>{stockLeft}</p>
        </div>
        <div className="summary-card daily-sales" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Stock Sold Today</h4>
          <p>{dailySales}</p>
        </div>
        <div className="summary-card total-revenue" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Total Revenue</h4>
          <p>M{totalRevenue}</p>
        </div>
      </div>

      <h3>Low Stock Products (less than 10)</h3>
      <table
        style={{
          width: '100%',
          borderCollapse: 'collapse',
          marginTop: '1rem',
        }}
      >
        <thead>
          <tr>
            <th style={{ padding: '0.5rem', borderBottom: '2px solid #000' }}>Name</th>
            <th style={{ padding: '0.5rem', borderBottom: '2px solid #000' }}>Stock Left</th>
          </tr>
        </thead>
        <tbody>
          {products.filter(p => (p.quantity || 0) < 10).map(item => (
            <tr key={item.id} style={{ backgroundColor: '#ffdddd' }}>
              <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.name}</td>
              <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.quantity}</td>
            </tr>
          ))}
          {products.filter(p => (p.quantity || 0) < 10).length === 0 && (
            <tr>
              <td colSpan="2" style={{ padding: '0.5rem', textAlign: 'center' }}>
                No low stock products.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default Reports;
