import React, { useEffect, useState } from 'react';
import api from '../api';

function Reports({ refreshSignal }) {
  const [loading, setLoading] = useState(true);
  const [products, setProducts] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const [prodRes, transRes] = await Promise.all([
          api.get('/products'),
          api.get('/transactions'),
        ]);
        setProducts(prodRes.data || []);
        setTransactions(transRes.data || []);
      } catch (error) {
        console.error('Error fetching data for reports:', error);
        setError('Failed to load reports: ' + (error.response?.data?.message || error.message));
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [refreshSignal]);

  if (loading) return <div>Loading reports...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  // Calculate total inventory value
  const totalInventoryValue = products.reduce((sum, p) => sum + (p.price * p.stock || 0), 0);

  // Group transactions by date (descending order)
  const sortedTransactions = transactions
    .slice()
    .sort((a, b) => new Date(b.date) - new Date(a.date));

  // Helper to get product name by ID
  const getProductName = (id) => {
    const product = products.find(p => p.id === id);
    return product ? product.name : 'Unknown Product';
  };

  return (
    <div className="dashboard">
      <h3>Reports</h3>

      <div className="card">
        <h4>Total Inventory Value: M{totalInventoryValue.toFixed(2)}</h4>
      </div>

      <div className="card" style={{ marginTop: '20px' }}>
        <h4>Transactions</h4>
        {sortedTransactions.length === 0 ? (
          <p>No transactions yet.</p>
        ) : (
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>Date</th>
                <th>Type</th>
                <th>Note</th>
                <th>Amount</th>
                <th>Product</th>
              </tr>
            </thead>
            <tbody>
              {sortedTransactions.map(txn => (
                <tr key={txn.id}>
                  <td>{txn.date}</td>
                  <td>{txn.type}</td>
                  <td>{txn.note}</td>
                  <td>{txn.amount}</td>
                  <td>{getProductName(txn.productId)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Reports;
