import React, { useEffect, useState } from 'react';
import api from './api';
import AddProductForm from './components/AddProductForm';
import ProductList from './components/ProductList';
import Dashboard from './components/Dashboard';
import UIDataList from './components/UIDataList';

function App() {
  const [products, setProducts] = useState([]);
  const [uiData, setUIData] = useState([]);

  const fetchProducts = async () => {
    const res = await api.get('/products');
    setProducts(res.data);
  };

  useEffect(() => {
    fetchProducts();
    const savedUIData = localStorage.getItem('uiData');
    if (savedUIData) setUIData(JSON.parse(savedUIData));
  }, []);

  const addUIData = (newData) => {
    const updatedUIData = [...uiData, { id: Date.now(), ...newData, timestamp: new Date().toISOString() }];
    setUIData(updatedUIData);
    localStorage.setItem('uiData', JSON.stringify(updatedUIData));
  };

  const exportUIData = () => {
    const dataStr = JSON.stringify(uiData, null, 2);
    const blob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'uiData.json';
    link.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="container">
      <h1>Boitumelo â€” Inventory</h1>
      <Dashboard products={products} uiData={uiData} />
      <div className="grid">
        <div>
          <AddProductForm onAdded={fetchProducts} addUIData={addUIData} />
          <button onClick={exportUIData} style={{ marginTop: '10px' }}>Export UI Data</button>
        </div>
        <div>
          <ProductList products={products} onChanged={fetchProducts} />
          <UIDataList uiData={uiData} />
        </div>
      </div>
    </div>
  );
}

export default App;
