import React, { useState, useEffect, useCallback } from 'react';
import api from '../api';

function Sales() {
  const [products, setProducts] = useState([]);
  const [salesData, setSalesData] = useState([]);
  const [filterDate, setFilterDate] = useState('');
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const [receipt, setReceipt] = useState(null);

  const fetchAll = useCallback(async () => {
    setLoading(true);
    try {
      const [prodRes, salesRes] = await Promise.all([
        api.get('/products'),
        api.get(filterDate ? `/sales?date=${filterDate}` : '/sales'),
      ]);
      setProducts(prodRes.data || []);
      setSalesData(salesRes.data || []);
    } catch (err) {
      console.error('Fetch error:', err);
    } finally {
      setLoading(false);
    }
  }, [filterDate]);

  useEffect(() => {
    fetchAll();
  }, [fetchAll]);

  // Build summary
  const dailySummary = products.map(p => {
    const soldQty = salesData
      .filter(s => s.productId === p.id)
      .reduce((sum, sale) => sum + Number(sale.quantity || 0), 0);

    const stockLeft = p.stock - soldQty;
    return { 
      id: p.id, 
      name: p.name, 
      initialStock: p.stock, 
      sold: soldQty, 
      stockLeft, 
      revenue: soldQty * p.price 
    };
  });

  const totalRevenue = dailySummary.reduce((sum, item) => sum + item.revenue, 0);
  const totalStockLeft = dailySummary.reduce((sum, item) => sum + (item.stockLeft || 0), 0);

  const availableStock = selectedProduct
    ? (products.find(p => p.id === selectedProduct)?.stock || 0)
      - (salesData.filter(s => s.productId === selectedProduct).reduce((sum, sale) => sum + Number(sale.quantity || 0), 0))
    : 0;

  const clampQuantity = (val) => {
    const num = parseInt(val, 10);
    if (isNaN(num)) return 1;
    return Math.min(Math.max(1, num), availableStock);
  };

  const sellProduct = async (e) => {
    e.preventDefault();
    if (!selectedProduct || quantity < 1 || quantity > availableStock) {
      return alert('Invalid selection or insufficient stock.');
    }

    const prod = products.find(p => p.id === selectedProduct);
    const sale = {
      productId: prod.id,
      name: prod.name,
      quantity,
      amount: prod.price * quantity,
      date: new Date().toISOString().split('T')[0],
    };

    try {
      await api.post('/sales', sale);
      setReceipt(sale);
      setSelectedProduct('');
      setQuantity(1);
      fetchAll();
    } catch (err) {
      console.error('Sale error:', err);
      alert('Sale failed.');
    }
  };

  if (loading) return (<div className="dashboard">Loading...</div>);

  return (
    <div className="dashboard">
      <div className="card">
        <h3>Sales Overview</h3>
        <label>
          Filter Date:&nbsp;
          <input
            type="date"
            value={filterDate}
            onChange={e => setFilterDate(e.target.value)}
          />
        </label>
        <table className="table" style={{ marginTop: 10 }}>
          <thead>
            <tr>
              <th>Product</th>
              <th>Initial Stock</th>
              <th>Sold</th>
              <th>Stock Left</th>
              <th>Revenue (M)</th>
            </tr>
          </thead>
          <tbody>
            {dailySummary.map(d => (
              <tr key={d.id} className={d.stockLeft < 10 ? 'low' : ''}>
                <td>{d.name}</td>
                <td>{d.initialStock}</td>
                <td>{d.sold}</td>
                <td>{d.stockLeft}</td>
                <td>{`M${d.revenue.toFixed(2)}`}</td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <td><strong>Totals</strong></td>
              <td>-</td><td>-</td>
              <td><strong>{totalStockLeft}</strong></td>
              <td><strong>{`M${totalRevenue.toFixed(2)}`}</strong></td>
            </tr>
          </tfoot>
        </table>
      </div>

      <div className="card" style={{ marginTop: 20 }}>
        <h3>Sell Product</h3>
        <form onSubmit={sellProduct}>
          <select
            value={selectedProduct}
            onChange={e => { setSelectedProduct(e.target.value); setQuantity(1); }}
            required
          >
            <option value="">Select Product</option>
            {products.map(p => (
              <option key={p.id} value={p.id} disabled={availableStock <= 0}>
                {p.name} — Available: {availableStock}
              </option>
            ))}
          </select>
          <input
            type="number"
            min="1"
            max={availableStock}
            value={quantity}
            onChange={e => setQuantity(clampQuantity(e.target.value))}
            required
            style={{ marginLeft: 10, width: 80 }}
          />
          <button type="submit" disabled={availableStock <= 0} style={{ marginLeft: 10 }}>
            Sell
          </button>
          {selectedProduct && (
            <span style={{ marginLeft: 10 }}>
              Available: {availableStock}
            </span>
          )}
        </form>
      </div>

      {receipt && (
        <div
          className="card receipt"
          style={{ marginTop: 20, padding: 15, border: '1px solid #ddd', maxWidth: 400 }}
        >
          <h4>Receipt</h4>
          <p><strong>Date:</strong> {new Date(receipt.date).toLocaleDateString()}</p>
          <p><strong>Product:</strong> {receipt.name}</p>
          <p><strong>Quantity:</strong> {receipt.quantity}</p>
          <p><strong>Total:</strong> {`M${receipt.amount.toFixed(2)}`}</p>
          <hr /><p>Thank you for your purchase!</p>
          <button onClick={() => setReceipt(null)} style={{ marginTop: 10 }}>Close Receipt</button>
        </div>
      )}
    </div>
  );
}

export default Sales;
