import React, { useEffect, useState } from 'react';
import api from '../api';

function Reports() {
  const [reportData, setReportData] = useState({
    totalSales: 0,
    totalStock: 0,
    lowStockItems: [],
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchReports = async () => {
      try {
        const res = await api.get('/reports');
        setReportData({
          totalSales: res.data.totalSales || 0,
          totalStock: res.data.totalStock || 0,
          lowStockItems: res.data.lowStockItems || [],
        });
      } catch (err) {
        console.error('Error fetching reports:', err);
      } finally {
        setLoading(false);
      }
    };
    fetchReports();
  }, []);

  if (loading) return <div className="dashboard">Loading reports...</div>;

  return (
    <div className="dashboard">
      <div className="summary-cards">
        <div className="summary-card total-items">
          <h4>Total Sales</h4>
          <p>${reportData.totalSales.toFixed(2)}</p>
        </div>
        <div className="summary-card total-stock">
          <h4>Total Stock</h4>
          <p>{reportData.totalStock}</p>
        </div>
        <div className="summary-card low-stock">
          <h4>Low Stock Items</h4>
          <p>{reportData.lowStockItems.length}</p>
        </div>
      </div>
      <div className="card">
        <h3>Low Stock Details</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Item Name</th>
              <th>Stock Level</th>
            </tr>
          </thead>
          <tbody>
            {reportData.lowStockItems.map(item => (
              <tr key={item.id}>
                <td>{item.name}</td>
                <td>{item.stock}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Reports;
