import React, { useEffect, useState } from 'react';
import api from './api';
import Dashboard from './components/Dashboard';
import Navbar from './components/Navbar';
import Reports from './components/Reports';
import Sales from './components/Sales';
import Inventory from './components/Inventory';
import Footer from './components/Footer';
import logo from './assets/logo.png';

function App() {
  const [products, setProducts] = useState([]);
  const [transactions, setTransactions] = useState([]); // Add transactions state
  const [activeSection, setActiveSection] = useState('dashboard');
  const [refreshSignal, setRefreshSignal] = useState(0);

  const fetchData = async () => {
    try {
      console.log('Fetching data...'); // Debug start
      const [productsRes, transactionsRes] = await Promise.all([
        api.get('/products'),
        api.get('/transactions'),
      ]);
      setProducts(productsRes.data || []);
      setTransactions(transactionsRes.data || []);
      console.log('Fetched products:', productsRes.data);
      console.log('Fetched transactions:', transactionsRes.data); // Debug transaction data
    } catch (error) {
      console.error('Failed to fetch data:', error);
      console.log('Fetch failed - using default empty arrays');
      setProducts([]);
      setTransactions([]);
    }
  };

  // Fetch data on mount and every time refreshSignal changes
  useEffect(() => {
    fetchData();
  }, [refreshSignal]);

  // Update stock after sale
  const updateProductQuantity = (productId, newStock) => {
    setProducts(prev =>
      prev.map(p => (p.id === productId ? { ...p, stock: newStock } : p))
    );
  };

  // Trigger refresh for products and reports
  const handleRefresh = () => {
    fetchData(); // Re-fetch data
    setRefreshSignal(prev => prev + 1);
    console.log('Refresh triggered, new signal:', prev + 1); // Debug refresh
  };

  return (
    <div className="app" style={{ minHeight: '100vh', position: 'relative', paddingBottom: '60px' }}>
      <Navbar logo={logo} setActiveSection={setActiveSection} />
      <div className="main-content">
        <div className="content">
          {activeSection === 'dashboard' && <Dashboard products={products} />}
          {activeSection === 'inventory' && (
            <Inventory products={products} onChanged={handleRefresh} />
          )}
          {activeSection === 'reports' && (
            <Reports refreshSignal={refreshSignal} transactions={transactions} />
          )}
          {activeSection === 'sales' && (
            <Sales
              products={products}
              updateProductQuantity={updateProductQuantity}
              onSold={handleRefresh}
            />
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
