import React, { useState, useEffect } from 'react';
import api from '../api';

export default function ProductList({ onChanged }) {
  const [products, setProducts] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [form, setForm] = useState({});

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await api.get('/products');
      setProducts(res.data);
    } catch (err) {
      console.error('Failed to fetch products:', err);
    }
  };

  const startEdit = (p) => {
    setEditingId(p.id);
    setForm({ ...p });
  };

  const cancelEdit = () => {
    setEditingId(null);
    setForm({});
  };

  const saveEdit = async () => {
    try {
      await api.put(`/products/${editingId}`, {
        ...form,
        price: Number(form.price),
        quantity: Number(form.quantity)
      });
      setEditingId(null);
      fetchProducts(); // Refresh products
      if (onChanged) onChanged();
    } catch (err) {
      console.error(err);
      alert('Failed to update');
    }
  };

  const deleteProduct = async (id) => {
    if (!window.confirm('Delete product?')) return;
    try {
      await api.delete(`/products/${id}`);
      fetchProducts(); // Refresh products
      if (onChanged) onChanged();
    } catch (err) {
      console.error(err);
      alert('Failed to delete');
    }
  };

  const transact = async (id, type) => {
    const amount = Number(prompt(`Enter amount to ${type}:`));
    if (!amount || amount <= 0) return alert('Invalid amount');
    try {
      await api.post(`/products/${id}/transaction`, { type, amount });
      fetchProducts(); // Refresh products
      if (onChanged) onChanged();
    } catch (err) {
      console.error(err);
      alert('Failed to record transaction');
    }
  };

  return (
    <div className="card">
      <h3>Products</h3>
      <table className="table">
        <thead>
          <tr><th>Name</th><th>Category</th><th>Price</th><th>Quantity</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id} className={Number(p.quantity) <= 5 ? 'low' : ''}>
              <td>
                {editingId === p.id ? (
                  <input value={form.name} onChange={e => setForm(f => ({ ...f, name: e.target.value }))} />
                ) : p.name}
              </td>
              <td>
                {editingId === p.id ? (
                  <input value={form.category} onChange={e => setForm(f => ({ ...f, category: e.target.value }))} />
                ) : p.category}
              </td>
              <td>
                {editingId === p.id ? (
                  <input type="number" value={form.price} onChange={e => setForm(f => ({ ...f, price: e.target.value }))} />
                ) : p.price}
              </td>
              <td>
                {editingId === p.id ? (
                  <input type="number" value={form.quantity} onChange={e => setForm(f => ({ ...f, quantity: e.target.value }))} />
                ) : p.quantity}
              </td>
              <td>
                {editingId === p.id ? (
                  <>
                    <button onClick={saveEdit}>Save</button>
                    <button onClick={cancelEdit}>Cancel</button>
                  </>
                ) : (
                  <>
                    <button onClick={() => startEdit(p)}>Edit</button>
                    <button onClick={() => deleteProduct(p.id)}>Delete</button>
                    <button onClick={() => transact(p.id, 'add')}>+Stock</button>
                    <button onClick={() => transact(p.id, 'deduct')}>-Sold</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
