import React, { useState, useEffect } from 'react';
import api from '../api';

function Sales({ products = [], onChanged }) {
  const [salesData, setSalesData] = useState([]);
  const [filterDate, setFilterDate] = useState('');
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const [receipt, setReceipt] = useState(null);

  // Fetch sales data only (filtered by date)
  useEffect(() => {
    const fetchSales = async () => {
      setLoading(true);
      try {
        const url = filterDate ? `/sales?date=${filterDate}` : '/sales';
        const res = await api.get(url);
        setSalesData(res.data || []);
      } catch (err) {
        console.error('Failed to fetch sales:', err);
      }
      setLoading(false);
    };
    fetchSales();
  }, [filterDate]);

  // Calculate sold quantity and stock left per product
  const dailySummary = products.map(p => {
    const sold = salesData
      .filter(s => s.productId === p.id)
      .reduce((sum, s) => sum + (s.quantity || 0), 0);
    const stockLeft = p.stock - sold;
    return { ...p, sold, stockLeft, revenue: sold * p.price };
  });

  const totalRevenue = dailySummary.reduce((sum, p) => sum + (p.revenue || 0), 0);
  const totalStockLeft = dailySummary.reduce((sum, p) => sum + (p.stockLeft || 0), 0);

  const availableStock = selectedProduct
    ? dailySummary.find(p => p.id === selectedProduct)?.stockLeft || 0
    : 0;

  // Handle selling product
  const sellProduct = async e => {
    e.preventDefault();
    if (!selectedProduct || quantity < 1 || quantity > availableStock) {
      return alert('Invalid quantity or product.');
    }

    const p = products.find(p => p.id === selectedProduct);
    const sale = {
      productId: selectedProduct,
      name: p.name,
      quantity,
      amount: p.price * quantity,
      date: new Date().toISOString().split('T')[0],
    };

    try {
      await api.post('/sales', sale);
      setReceipt(sale);
      setSelectedProduct('');
      setQuantity(1);
      if (onChanged) onChanged();  // Refresh products & sales in parent
    } catch (err) {
      console.error(err);
      alert('Sale failed.');
    }
  };

  if (loading) return <div className="dashboard">Loading sales data...</div>;

  return (
    <div className="dashboard">
      <div className="card">
        <h3>Sales Overview</h3>
        <label>
          Filter by Date:{' '}
          <input
            type="date"
            value={filterDate}
            onChange={e => setFilterDate(e.target.value)}
          />
        </label>

        <table className="table" style={{ marginTop: 10 }}>
          <thead>
            <tr>
              <th>Product</th>
              <th>Initial Stock</th>
              <th>Sold</th>
              <th>Stock Left</th>
              <th>Revenue</th>
            </tr>
          </thead>
          <tbody>
            {dailySummary.map(p => (
              <tr key={p.id} className={p.stockLeft < 10 ? 'low' : ''}>
                <td>{p.name}</td>
                <td>{p.stock}</td>
                <td>{p.sold}</td>
                <td>{p.stockLeft}</td>
                <td>{`M${p.revenue.toFixed(2)}`}</td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <td><strong>Totals</strong></td>
              <td>-</td>
              <td>-</td>
              <td><strong>{totalStockLeft}</strong></td>
              <td><strong>{`M${totalRevenue.toFixed(2)}`}</strong></td>
            </tr>
          </tfoot>
        </table>
      </div>

      <div className="card" style={{ marginTop: 20 }}>
        <h3>Sell Product</h3>
        <form onSubmit={sellProduct} style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <select
            value={selectedProduct}
            onChange={e => {
              setSelectedProduct(e.target.value);
              setQuantity(1);
            }}
            required
          >
            <option value="">Select Product</option>
            {dailySummary.map(p => (
              <option key={p.id} value={p.id} disabled={p.stockLeft <= 0}>
                {p.name} — Stock Left: {p.stockLeft}
              </option>
            ))}
          </select>

          <input
            type="number"
            min="1"
            max={availableStock}
            value={quantity}
            onChange={e => {
              let val = parseInt(e.target.value, 10);
              if (isNaN(val)) val = 1;
              val = Math.min(Math.max(1, val), availableStock);
              setQuantity(val);
            }}
            required
            style={{ width: '80px' }}
            disabled={availableStock === 0}
          />

          <button type="submit" disabled={availableStock === 0}>
            Sell
          </button>
          {selectedProduct && <span>Available: {availableStock}</span>}
        </form>
      </div>

      {receipt && (
        <div
          className="card receipt"
          style={{
            margin: '20px 0',
            padding: 15,
            border: '1px solid #ddd',
            maxWidth: 400,
          }}
        >
          <h4>Receipt</h4>
          <p><strong>Date:</strong> {new Date(receipt.date).toLocaleDateString()}</p>
          <p><strong>Product:</strong> {receipt.name}</p>
          <p><strong>Quantity:</strong> {receipt.quantity}</p>
          <p><strong>Total:</strong> {`M${receipt.amount.toFixed(2)}`}</p>
          <hr />
          <p>Thank you!</p>
          <button onClick={() => setReceipt(null)} style={{ marginTop: 10 }}>
            Close
          </button>
        </div>
      )}
    </div>
  );
}

export default Sales;
