import React, { useState, useEffect } from 'react';
import api from '../api';

function Sales() {
  const [products, setProducts] = useState([]);
  const [selling, setSelling] = useState({});
  const [loading, setLoading] = useState(true);

  // Fetch products
  const fetchProducts = async () => {
    try {
      const res = await api.get('/products');
      setProducts(res.data || []);
    } catch (err) {
      console.error('Failed to fetch products:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleQuantityChange = (productId, value) => {
    const stock = products.find(p => p.id === productId)?.stock || 1;
    const num = Math.max(1, Math.min(parseInt(value, 10) || 1, stock));
    setSelling(prev => ({ ...prev, [productId]: num }));
  };

  const sellProduct = async (productId) => {
    const quantity = selling[productId] || 1;
    const product = products.find(p => p.id === productId);
    if (!product) return alert('Product not found');
    if (quantity > product.stock) return alert('Quantity exceeds available stock');

    const sale = {
      productId,
      name: product.name,
      quantity,
      amount: product.price * quantity,
      date: new Date().toISOString().split('T')[0],
    };

    try {
      await api.post('/sales', sale);
      await api.put(`/products/${productId}`, { stock: product.stock - quantity });
      setProducts(prev =>
        prev.map(p => (p.id === productId ? { ...p, stock: p.stock - quantity } : p))
      );
      setSelling(prev => ({ ...prev, [productId]: 1 }));
      alert(`Sold ${quantity} x ${product.name}`);
    } catch (err) {
      if (err.response) {
        // Server returned error status code
        console.error('Server error:', err.response.status, err.response.data);
        alert(`Server error: ${err.response.status} - ${JSON.stringify(err.response.data)}`);
      } else if (err.request) {
        // Network error or no response
        console.error('No response:', err.request);
        alert('No response from server. Check your network or server.');
      } else {
        // Other unexpected error
        console.error('Error:', err.message);
        alert(`Error: ${err.message}`);
      }
    }
  };

  if (loading) return <div>Loading products...</div>;

  return (
    <div className="dashboard">
      <h3>Sales</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 15 }}>
        {products.map(product => (
          <div key={product.id} className="card" style={{ width: 250, padding: 15 }}>
            <h4>{product.name}</h4>
            <p>Price: M{product.price.toFixed(2)}</p>
            <p>Stock: {product.stock}</p>
            <input
              type="number"
              min="1"
              max={product.stock}
              value={selling[product.id] || 1}
              onChange={e => handleQuantityChange(product.id, e.target.value)}
              disabled={product.stock === 0}
              style={{ width: '80px', marginBottom: 8 }}
            />
            <button
              onClick={() => sellProduct(product.id)}
              disabled={product.stock === 0}
              style={{ width: '100%' }}
            >
              {product.stock === 0 ? 'Out of Stock' : 'Sell'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Sales;
