import React, { useEffect, useState } from 'react';
import api from '../api';

export default function StockTransactions({ onRefresh }) {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    console.log('Refetching transactions, onRefresh:', onRefresh); // Debug log
    setLoading(true);
    api.get('/transactions')
      .then(res => {
        console.log('Transactions fetched:', res.data); // Debug log
        setTransactions(res.data);
      })
      .catch(err => {
        console.error('Failed to fetch transactions:', err); // Debug log
      })
      .finally(() => setLoading(false));
  }, [onRefresh]);

  if (loading) {
    return <div>Loading transactions...</div>;
  }

  if (transactions.length === 0) {
    return <div>No transactions found.</div>;
  }

  return (
    <div className="card">
      <h3>Stock Transactions</h3>
      <table className="table">
        <thead>
          <tr><th>ID</th><th>Product ID</th><th>Type</th><th>Amount</th><th>Date</th><th>Note</th></tr>
        </thead>
        <tbody>
          {transactions.map(t => (
            <tr key={t.id}>
              <td>{t.id}</td>
              <td>{t.productId}</td>
              <td>{t.type}</td>
              <td>{t.amount}</td>
              <td>{t.date}</td>
              <td>{t.note}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
