import React, { useEffect, useState } from 'react';
import api from './api';
import Dashboard from './components/Dashboard';
import Navbar from './components/Navbar';
import Reports from './components/Reports';
import Sales from './components/Sales';
import Inventory from './components/Inventory';
import Footer from './components/Footer';
import logo from './assets/logo.png';

function App() {
  const [products, setProducts] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [activeSection, setActiveSection] = useState('dashboard');
  const [refreshSignal, setRefreshSignal] = useState(0);

  const fetchData = async () => {
    try {
      const [productsRes, transactionsRes] = await Promise.all([
        api.get('/products'),
        api.get('/transactions'), // Add this endpoint if not present
      ]);
      setProducts(productsRes.data || []);
      setTransactions(transactionsRes.data || []);
    } catch (error) {
      console.error('Failed to fetch data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const updateProductQuantity = (productId, newQuantity) => {
    setProducts(prev =>
      prev.map(p => (p.id === productId ? { ...p, quantity: newQuantity } : p))
    );
  };

  const addTransaction = async (transactionData) => {
    try {
      const response = await api.post('/products/:id/transaction', transactionData); // Adjust endpoint
      setTransactions(prev => [...prev, response.data.transaction]);
    } catch (error) {
      console.error('Failed to add transaction:', error);
    }
  };

  const refreshData = () => {
    setRefreshSignal(prev => prev + 1);
  };

  return (
    <div className="app" style={{ minHeight: '100vh', position: 'relative', paddingBottom: '60px' }}>
      <Navbar logo={logo} setActiveSection={setActiveSection} />
      <div className="main-content">
        <div className="content">
          {activeSection === 'dashboard' && <Dashboard products={products} />}
          {activeSection === 'inventory' && <Inventory products={products} onChanged={refreshData} />}
          {activeSection === 'reports' && <Reports refreshSignal={refreshSignal} />}
          {activeSection === 'sales' && (
            <Sales
              products={products}
              updateProductQuantity={updateProductQuantity}
              addTransaction={addTransaction}
              onChanged={refreshData}
            />
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
