import React, { useEffect, useState } from 'react';
import api from '../api';

function Reports({ refreshSignal }) {
  const [loading, setLoading] = useState(true);
  const [products, setProducts] = useState([]);
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [prodRes, transRes] = await Promise.all([
          api.get('/products'),
          api.get('/transactions'),
        ]);
        setProducts(prodRes.data || []);
        setTransactions(transRes.data || []);
      } catch (error) {
        console.error('Error fetching data for reports:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [refreshSignal]); // refetch when refreshSignal changes

  if (loading) return <div className="dashboard">Loading reports...</div>;

  // Calculate total sales (sum of all transaction amounts)
  const totalSales = transactions.reduce((acc, txn) => acc + (txn.amount || 0), 0);

  // Calculate total stock (sum of product stocks)
  const totalStock = products.reduce((acc, product) => acc + (product.stock || 0), 0);

  // Define low stock threshold (e.g., stock < 10)
  const LOW_STOCK_THRESHOLD = 10;

  // Filter products with low stock
  const lowStockItems = products.filter(product => (product.stock || 0) < LOW_STOCK_THRESHOLD);

  return (
    <div className="dashboard">
      <div className="summary-cards" style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
        <div className="summary-card total-sales" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Total Sales</h4>
          <p>${totalSales.toFixed(2)}</p>
        </div>
        <div className="summary-card total-stock" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Total Stock</h4>
          <p>{totalStock}</p>
        </div>
        <div className="summary-card low-stock" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Low Stock Items</h4>
          <p>{lowStockItems.length}</p>
        </div>
      </div>
      <div className="card" style={{ padding: '1rem', border: '1px solid #ccc' }}>
        <h3>Low Stock Details</h3>
        {lowStockItems.length === 0 ? (
          <p>All items are sufficiently stocked.</p>
        ) : (
          <table className="table" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Item Name</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Stock Level</th>
              </tr>
            </thead>
            <tbody>
              {lowStockItems.map(item => (
                <tr key={item.id}>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.name}</td>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.stock}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Reports;
