import React, { useEffect, useState } from 'react';
import api from '../api';

function Reports({ refreshSignal, transactions }) {
  const [loading, setLoading] = useState(true);
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log('Refresh signal changed:', refreshSignal); // Debug log
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const prodRes = await api.get('/products');
        setProducts(prodRes.data || []);
      } catch (error) {
        console.error('Error fetching products for reports:', error);
        setError('Failed to fetch product data.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [refreshSignal]);

  // Current date for filtering today's transactions
  const today = new Date().toISOString().split('T')[0];

  // Calculate metrics using passed transactions prop
  const totalSales = transactions.reduce((acc, txn) => acc + (txn.amount || 0), 0).toFixed(2);
  const totalProducts = products.length;
  const amountOfSale = transactions
    .filter(t => t.date === today && t.type === 'deduct' && t.note === 'sold')
    .reduce((acc, txn) => acc + (txn.amount || 0), 0).toFixed(2);

  // Calculate product details including sold quantity and available quantity
  const productsWithDetails = products.map(product => {
    const soldQuantity = transactions
      .filter(t => t.productId === product.id && t.type === 'deduct' && t.note === 'sold')
      .reduce((acc, txn) => acc + (txn.amount || 0), 0);
    const availableQuantity = Math.max(0, (product.stock || 0) - soldQuantity);
    return {
      name: product.name,
      price: Number(product.price || 0).toFixed(2),
      initialQuantity: product.stock || 0,
      soldQuantity,
      availableQuantity,
    };
  });

  if (loading) return <div className="dashboard">Loading reports...</div>;
  if (error) return <div className="dashboard">Error: {error}</div>;

  return (
    <div className="dashboard">
      <div className="summary-cards" style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
        <div className="summary-card total-sales" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Total Sales</h4>
          <p>M{totalSales}</p>
        </div>
        <div className="summary-card total-products" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Total Products</h4>
          <p>{totalProducts}</p>
        </div>
        <div className="summary-card amount-of-sale" style={{ flex: 1, padding: '1rem', border: '1px solid #ccc' }}>
          <h4>Amount of Sale Today</h4>
          <p>M{amountOfSale}</p>
        </div>
      </div>
      <div className="card" style={{ padding: '1rem', border: '1px solid #ccc', marginBottom: '1rem' }}>
        <h3>Product Details</h3>
        {productsWithDetails.length === 0 ? (
          <p>No products available.</p>
        ) : (
          <table className="table" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Product Name</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Price (M)</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Initial Quantity</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Sold Quantity</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Available Quantity</th>
              </tr>
            </thead>
            <tbody>
              {productsWithDetails.map((item, index) => (
                <tr key={index}>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.name}</td>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.price}</td>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.initialQuantity}</td>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.soldQuantity}</td>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.availableQuantity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
      <div className="card" style={{ padding: '1rem', border: '1px solid #ccc' }}>
        <h3>Products Available</h3>
        {productsWithDetails.length === 0 ? (
          <p>No products available.</p>
        ) : (
          <table className="table" style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Product Name</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Price (M)</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Available Quantity</th>
              </tr>
            </thead>
            <tbody>
              {productsWithDetails.map((item, index) => (
                <tr key={index}>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.name}</td>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.price}</td>
                  <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{item.availableQuantity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Reports;
