import React, { useState, useEffect } from 'react';
// eslint-disable-next-line no-unused-vars
import api from '../api'; // Kept with suppression

export default function Dashboard({ products: initialProducts = [], onStockChange }) {
  const [products, setProducts] = useState(initialProducts);
  const [editStock, setEditStock] = useState({}); // { productId: editingValue }

  useEffect(() => {
    setProducts(initialProducts);
  }, [initialProducts]);

  const totalItems = products.length;
  const totalStock = products.reduce((s, p) => s + Number(p.quantity || 0), 0);
  const lowStock = products.filter(p => Number(p.quantity || 0) <= 5).length;

  const handleImageError = (e) => {
    e.target.src = 'https://via.placeholder.com/150?text=Image+Not+Available';
  };

  const handleStockChange = (id, value) => {
    if (/^\d*$/.test(value)) {
      setEditStock(prev => ({ ...prev, [id]: value }));
    }
  };

  return (
    <div className="dashboard card">
      <h3>Dashboard Overview</h3>
      <div className="summary-cards" style={{ display: 'flex', gap: '1rem' }}>
        <div className="summary-card" style={{ flex: 1 }}>
          <h4>Total Items</h4>
          <p><strong>{totalItems}</strong></p>
        </div>
        <div className="summary-card" style={{ flex: 1 }}>
          <h4>Total Stock</h4>
          <p><strong>{totalStock}</strong></p>
        </div>
        <div className="summary-card" style={{ flex: 1 }}>
          <h4>Low Stock</h4>
          <p><strong>{lowStock}</strong></p>
        </div>
      </div>

      <div className="product-cards" style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem', marginTop: '1rem' }}>
        {products.slice(0, 5).map(p => {
          const isLowStock = Number(p.quantity || 0) <= 5; // Threshold of 5
          const currentQuantity = editStock[p.id] !== undefined ? editStock[p.id] : p.quantity;
          return (
            <div
              key={p.id}
              className="product-card"
              style={{
                border: '1px solid #ddd',
                padding: 10,
                width: 200,
                backgroundColor: isLowStock ? '#ffcccc' : 'transparent',
              }}
            >
              <img
                src={p.image || 'https://via.placeholder.com/150?text=No+Image'} // Default image for missing images
                alt={p.name}
                className="product-image"
                style={{
                  width: '100%', // Adjusted to fit card width
                  height: '150px', // Reasonable height to fit within 200px width
                  objectFit: 'cover',
                  margin: '0 5px 10px 5px', // Add spacing (left, right, bottom, top)
                }}
                onError={handleImageError}
              />
              <div className="product-info" style={{ marginTop: 10 }}>
                <h4>{p.name}</h4>
                <p>Price: M{Number(p.price || 0).toFixed(2)}</p>
                <div>
                  <label>
                    Quantity:&nbsp;
                    <input
                      type="text"
                      value={currentQuantity}
                      onChange={(e) => handleStockChange(p.id, e.target.value)}
                      style={{ width: '60px' }}
                      readOnly
                    />
                  </label>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
