import React, { useEffect, useState } from 'react';
import api from '../api';

function Sales({ refreshSignal, transactions = [] }) {
  const [loading, setLoading] = useState(true);
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      setError(null);
      try {
        const prodRes = await api.get('/products');
        if (!Array.isArray(prodRes.data)) {
          throw new Error('Products data is not an array');
        }
        setProducts(prodRes.data || []);
      } catch (error) {
        setError(error.message || 'Failed to fetch products');
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [refreshSignal]);

  if (loading) return <div>Loading sales...</div>;
  if (error) return <div>Error: {error}</div>;

  // Filter transactions to only show sales (assuming type='deduct' and note='sold')
  const saleTransactions = (transactions || []).filter(
    t => t.type === 'deduct' && t.note === 'sold'
  );

  return (
    <div className="sales-container">
      {/* Existing sales UI here */}

      {/* Receipts Section */}
      <div className="receipts" style={{ marginTop: '2rem' }}>
        <h3>Sales Receipts</h3>
        {saleTransactions.length === 0 ? (
          <p>No sales transactions available.</p>
        ) : (
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Product</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Quantity</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Price per unit</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Total</th>
                <th style={{ borderBottom: '1px solid #ccc', padding: '0.5rem' }}>Date</th>
              </tr>
            </thead>
            <tbody>
              {saleTransactions.map((txn, index) => {
                const product = products.find(p => p.id === txn.productId) || {};
                const price = Number(product.price || 0);
                const quantity = txn.amount || 0;
                const total = (price * quantity).toFixed(2);
                const date = new Date(txn.date).toLocaleString();

                return (
                  <tr key={index}>
                    <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{product.name || 'Unknown'}</td>
                    <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{quantity}</td>
                    <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>M{price.toFixed(2)}</td>
                    <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>M{total}</td>
                    <td style={{ padding: '0.5rem', borderBottom: '1px solid #eee' }}>{date}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Sales;
