import React, { useState } from 'react';
import api from '../api';

function Sales({ products, updateProductQuantity }) {
  const [selling, setSelling] = useState({});

  const handleQuantityChange = (productId, value) => {
    const product = products.find(p => p.id === productId);
    const stock = Number(product?.quantity || 0);
    const num = Math.max(1, Math.min(parseInt(value, 10) || 1, stock));
    setSelling(prev => ({ ...prev, [productId]: num }));
  };

  const sellProduct = async (productId) => {
    const quantity = selling[productId] || 1;
    const product = products.find(p => p.id === productId);
    if (!product) return alert('Product not found');
    if (quantity > product.quantity) return alert('Quantity exceeds available stock');

    const sale = {
      productId,
      name: product.name,
      quantity,
      amount: product.price * quantity,
      date: new Date().toISOString().split('T')[0],
    };

    try {
      await api.post('/transactions', sale);

      // Update product quantity on server
      const updatedProduct = { ...product, quantity: product.quantity - quantity };
      await api.put(`/products/${productId}`, updatedProduct);

      // Update product quantity in App state (lifting state up)
      updateProductQuantity(productId, updatedProduct.quantity);

      setSelling(prev => ({ ...prev, [productId]: 1 }));

      alert(`Sold ${quantity} x ${product.name} successfully!`);
    } catch (err) {
      console.error('Sale failed:', err);
      alert('Sale failed. Check console for details.');
    }
  };

  if (products.length === 0) return <div>No products available</div>;

  return (
    <div className="dashboard">
      <h3>Sales</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 15 }}>
        {products.map(product => (
          <div
            key={product.id}
            className="card"
            style={{ width: 250, padding: 15, border: '1px solid #ccc', borderRadius: 8 }}
          >
            {product.image && (
              <img
                src={product.image}
                alt={product.name}
                style={{ width: '100%', height: 150, objectFit: 'cover', marginBottom: 10 }}
                onError={(e) => e.target.src = 'https://via.placeholder.com/150?text=No+Image'}
              />
            )}
            <h4>{product.name}</h4>
            <p>Price: M{product.price.toFixed(2)}</p>
            <p>Stock: {product.quantity}</p>
            <input
              type="number"
              min="1"
              max={product.quantity}
              value={selling[product.id] || 1}
              onChange={e => handleQuantityChange(product.id, e.target.value)}
              disabled={product.quantity === 0}
              style={{ width: '80px', marginBottom: 8 }}
            />
            <button
              onClick={() => sellProduct(product.id)}
              disabled={product.quantity === 0}
              style={{
                width: '100%',
                backgroundColor: product.quantity === 0 ? '#ccc' : '#007bff',
                color: '#fff',
                border: 'none',
                padding: '8px',
                cursor: product.quantity === 0 ? 'not-allowed' : 'pointer',
              }}
            >
              {product.quantity === 0 ? 'Out of Stock' : 'Sell'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Sales;
