import React, { useEffect, useState } from 'react';
import api from '../api';

export default function StockTransactions({ onRefresh }) {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    // Fetch transactions whenever onRefresh changes
    api.get('/transactions')
      .then(res => setTransactions(res.data))
      .catch(err => console.error('Failed to fetch transactions:', err)); // Log errors for debugging
  }, [onRefresh]); // Re-run effect when onRefresh changes

  return (
    <div className="card">
      <h3>Stock Transactions</h3>
      <table className="table">
        <thead>
          <tr><th>ID</th><th>Product ID</th><th>Type</th><th>Amount</th><th>Date</th><th>Note</th></tr>
        </thead>
        <tbody>
          {transactions.map(t => (
            <tr key={t.id}>
              <td>{t.id}</td>
              <td>{t.productId}</td>
              <td>{t.type}</td>
              <td>{t.amount}</td>
              <td>{t.date}</td>
              <td>{t.note}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
