import React, { useState, useEffect } from 'react';

export default function Dashboard({ products: initialProducts = [], onStockChange }) {
  const [products, setProducts] = useState(initialProducts);
  const [editStock, setEditStock] = useState({}); // { productId: editingValue }

  useEffect(() => {
    setProducts(initialProducts);
    setEditStock({}); // reset edit fields on products update
  }, [initialProducts]);

  const totalItems = products.length;
  const totalStock = products.reduce((s, p) => s + Number(p.stock || 0), 0);
  const lowStock = products.filter(p => Number(p.stock || 0) <= 5).length;

  const handleImageError = (e) => {
    e.target.src = 'https://via.placeholder.com/150?text=Image+Not+Available';
  };

  const handleStockChange = (id, value) => {
    if (/^\d*$/.test(value)) {
      setEditStock(prev => ({ ...prev, [id]: value }));
    }
  };

  const handleSaveStock = (id) => {
    const newStock = parseInt(editStock[id], 10);
    if (isNaN(newStock) || newStock < 0) {
      alert('Please enter a valid stock quantity.');
      return;
    }

    // Inform parent of stock change
    if (onStockChange) {
      onStockChange(id, newStock);
    }

    // Update local product list immediately for UI responsiveness
    setProducts(prev =>
      prev.map(p => (p.id === id ? { ...p, stock: newStock } : p))
    );

    // Clear edit state for this product
    setEditStock(prev => {
      const copy = { ...prev };
      delete copy[id];
      return copy;
    });
  };

  return (
    <div className="dashboard card">
      <h3>Dashboard Overview</h3>
      <div className="summary-cards" style={{ display: 'flex', gap: '1rem' }}>
        <div className="summary-card" style={{ flex: 1 }}>
          <h4>Total Items</h4>
          <p><strong>{totalItems}</strong></p>
        </div>
        <div className="summary-card" style={{ flex: 1 }}>
          <h4>Total Stock</h4>
          <p><strong>{totalStock}</strong></p>
        </div>
        <div className="summary-card" style={{ flex: 1 }}>
          <h4>Low Stock</h4>
          <p><strong>{lowStock}</strong></p>
        </div>
      </div>

      <div className="product-cards" style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem', marginTop: '1rem' }}>
        {products.slice(0, 5).map(p => {
          const isEditing = editStock[p.id] !== undefined;
          return (
            <div key={p.id} className="product-card" style={{ border: '1px solid #ddd', padding: 10, width: 200 }}>
              <img
                src={p.image || 'https://via.placeholder.com/150?text=No+Image'}
                alt={p.name}
                className="product-image"
                style={{ width: '100%', height: 150, objectFit: 'cover' }}
                onError={handleImageError}
              />
              <div className="product-info" style={{ marginTop: 10 }}>
                <h4>{p.name}</h4>
                <p>Price: M{Number(p.price || 0).toFixed(2)}</p>
                <div>
                  <label>
                    Quantity:&nbsp;
                    <input
                      type="text"
                      value={isEditing ? editStock[p.id] : p.stock}
                      onChange={(e) => handleStockChange(p.id, e.target.value)}
                      style={{ width: '60px' }}
                    />
                  </label>
                  {isEditing && (
                    <button
                      onClick={() => handleSaveStock(p.id)}
                      style={{ marginLeft: '8px' }}
                    >
                      Save
                    </button>
                  )}
                  {!isEditing && (
                    <button
                      onClick={() => setEditStock(prev => ({ ...prev, [p.id]: p.stock.toString() }))}
                      style={{ marginLeft: '8px' }}
                    >
                      Edit
                    </button>
                  )}
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
