import React, { useEffect, useState } from 'react';
import api from '../api';

function Report() {
  const [products, setProducts] = useState([]);
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [productRes, transactionRes] = await Promise.all([
          api.get('/products'),
          api.get('/transactions'),
        ]);
        setProducts(productRes.data || []);
        setTransactions(transactionRes.data || []);
      } catch (err) {
        console.error('Failed to fetch report data:', err);
        alert('Failed to load report data.');
      }
    };

    fetchData();
  }, []);

  // Filter only 'sold' transactions
  const sales = transactions.filter(tx => tx.note === 'sold' && tx.type === 'deduct');

  // Calculate quantity sold per product
  const salesByProduct = sales.reduce((acc, tx) => {
    acc[tx.productId] = (acc[tx.productId] || 0) + tx.amount;
    return acc;
  }, {});

  // Calculate total sales amount
  const totalSalesAmount = products.reduce((sum, product) => {
    const qtySold = salesByProduct[product.id] || 0;
    return sum + qtySold * product.price;
  }, 0);

  return (
    <div className="dashboard">
      <h3>Sales & Inventory Report</h3>
      <h4>Total Sales Amount: M{totalSalesAmount.toFixed(2)}</h4>

      <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: 20 }}>
        <thead>
          <tr>
            <th style={thStyle}>Product</th>
            <th style={thStyle}>Price (M)</th>
            <th style={thStyle}>Stock Left</th>
            <th style={thStyle}>Quantity Sold</th>
            <th style={thStyle}>Sales Amount (M)</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => {
            const qtySold = salesByProduct[product.id] || 0;
            const salesAmount = qtySold * product.price;
            return (
              <tr key={product.id}>
                <td style={tdStyle}>{product.name}</td>
                <td style={tdStyle}>M{product.price.toFixed(2)}</td>
                <td style={tdStyle}>{product.stock}</td>
                <td style={tdStyle}>{qtySold}</td>
                <td style={tdStyle}>M{salesAmount.toFixed(2)}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

const thStyle = {
  border: '1px solid #ccc',
  padding: 8,
  background: '#f0f0f0',
};

const tdStyle = {
  border: '1px solid #ccc',
  padding: 8,
};

export default Report;
