import React, { useEffect, useState, useCallback } from 'react';
import api from '../api';

function Sales({ products: initialProducts }) {
  const [products, setProducts] = useState(initialProducts || []);
  const [salesData, setSalesData] = useState([]);
  const [dailySummary, setDailySummary] = useState({});
  const [filterDate] = useState(''); // setFilterDate removed since it's unused
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [totalSales, setTotalSales] = useState(0);
  const [loading, setLoading] = useState(true);

  const dateKey = filterDate || new Date().toISOString().split('T')[0];

  useEffect(() => {
    setProducts(initialProducts || []);
    setLoading(false);
  }, [initialProducts]);

  // ✅ Wrapped in useCallback to prevent ESLint warning
  const fetchSales = useCallback(async () => {
    try {
      const res = await api.get('/sales' + (filterDate ? `?date=${filterDate}` : ''));
      const sales = res.data || [];
      setSalesData(sales);
      processDailySummary(sales);
    } catch (err) {
      console.error('Error fetching sales:', err);
    }
  }, [filterDate, products]);

  useEffect(() => {
    fetchSales();
  }, [fetchSales]);

  const processDailySummary = (sales) => {
    const summary = { starting: {}, sold: {}, remaining: {}, cashTotal: 0 };

    products.forEach(p => {
      const soldQty = sales
        .filter(s => s.productId === p.id)
        .reduce((sum, s) => sum + s.quantity, 0);

      summary.starting[p.id] = p.stock + soldQty;
      summary.sold[p.id] = soldQty;
      summary.remaining[p.id] = p.stock;
    });

    summary.cashTotal = sales.reduce((sum, s) => sum + s.amount, 0);
    setDailySummary(summary);
    setTotalSales(summary.cashTotal);
  };

  const sellProduct = async (e) => {
    e.preventDefault();
    const qty = parseInt(quantity, 10);
    if (!selectedProduct || qty <= 0) {
      alert('Please select a product and enter a valid quantity.');
      return;
    }

    const product = products.find(p => p.id === selectedProduct);
    if (!product || product.stock < qty) {
      alert('Insufficient stock or invalid product.');
      return;
    }

    try {
      const saleAmount = parseFloat(product.price) * qty;
      const saleData = {
        productId: selectedProduct,
        name: product.name,
        quantity: qty,
        amount: saleAmount,
        date: dateKey,
        id: Date.now() + Math.random(),
      };

      await api.post('/sales', saleData);

      const updatedProducts = products.map(p =>
        p.id === selectedProduct ? { ...p, stock: p.stock - qty } : p
      );
      setProducts(updatedProducts);

      let uiData = JSON.parse(localStorage.getItem('uiData') || '[]');
      const idx = uiData.findIndex(p => p.id === selectedProduct);
      if (idx !== -1) {
        uiData[idx].stock -= qty;
      } else {
        uiData.push({ ...product, stock: product.stock - qty });
      }
      localStorage.setItem('uiData', JSON.stringify(uiData));

      const newSales = [...salesData, saleData];
      setSalesData(newSales);
      processDailySummary(newSales);
      setQuantity(1);
      setSelectedProduct('');
      generateReceipt(saleData);
    } catch (err) {
      console.error('Error processing sale:', err);
      alert('Failed to record sale. Please try again.');
    }
  };

  const generateReceipt = (sale) => {
    const receiptText = `
      Receipt for Sale
      -----------------------
      Date: ${sale.date}
      Product: ${sale.name}
      Quantity: ${sale.quantity}
      Amount: $${sale.amount.toFixed(2)}

      Thank you for your purchase!
    `;
    const blob = new Blob([receiptText], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `receipt-${sale.id}.txt`;
    link.click();
    URL.revokeObjectURL(url);
  };

  if (loading) return <div className="dashboard">Loading sales...</div>;

  return (
    <div className="dashboard">

      {/* === Daily Summary === */}
      <div className="card">
        <h3>Daily Summary ({dateKey})</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Product</th>
              <th>Starting Qty</th>
              <th>Sold</th>
              <th>Remaining</th>
            </tr>
          </thead>
          <tbody>
            {products.map(p => (
              <tr key={p.id}>
                <td>{p.name}</td>
                <td>{dailySummary.starting[p.id] || 0}</td>
                <td>{dailySummary.sold[p.id] || 0}</td>
                <td>{dailySummary.remaining[p.id] || p.stock}</td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <th colSpan="3">Total Cash from Sales:</th>
              <th>${totalSales.toFixed(2)}</th>
            </tr>
          </tfoot>
        </table>
      </div>

      {/* === Sell Product === */}
      <div className="card">
        <h3>Sell Product</h3>
        <form onSubmit={sellProduct} className="add-product-form">
          <select
            name="selectedProduct"
            value={selectedProduct}
            onChange={(e) => { setSelectedProduct(e.target.value); setQuantity(1); }}
            required
          >
            <option value="">Select a Product</option>
            {products.map(p => (
              <option key={p.id} value={p.id}>
                {p.name} (Price: ${parseFloat(p.price).toFixed(2)}, Stock: {p.stock})
              </option>
            ))}
          </select>
          <input
            type="number"
            value={quantity}
            onChange={(e) =>
              setQuantity(Math.min(Math.max(1, parseInt(e.target.value) || 1), products.find(p => p.id === selectedProduct)?.stock || 1))
            }
            min="1"
            placeholder="Quantity"
            required
          />
          <button type="submit">Sell</button>
        </form>
      </div>

      {/* === Transactions Table === */}
      <div className="card">
        <h3>Sales Transactions</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Product</th>
              <th>Quantity</th>
              <th>Amount</th>
            </tr>
          </thead>
          <tbody>
            {salesData.map((sale) => (
              <tr key={sale.id}>
                <td>{new Date(sale.date).toLocaleDateString()}</td>
                <td>{sale.name}</td>
                <td>{sale.quantity}</td>
                <td>${sale.amount.toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

    </div>
  );
}

export default Sales;
