// StockTransactions.jsx
import React, { useEffect, useState } from 'react';
import api from '../api';

export default function StockTransactions({ onRefresh }) {
  const [transactions, setTransactions] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTransactions = () => {
      api
        .get('/transactions')
        .then(res => {
          console.log('Fetched transactions:', res.data); // Debug log
          setTransactions([...res.data]); // Ensure new array reference
          setError(null);
        })
        .catch(err => {
          console.error('Failed to fetch transactions:', err);
          setError('Failed to load transactions. Please try again.');
        });
    };

    fetchTransactions();
  }, [onRefresh]); // Re-run when onRefresh changes

  return (
    <div className="card">
      <h3>Stock Transactions</h3>
      {error && <p className="error" style={{ color: 'red' }}>{error}</p>}
      {transactions.length === 0 && !error ? (
        <p>No transactions available.</p>
      ) : (
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Product ID</th>
              <th>Type</th>
              <th>Amount</th>
              <th>Date</th>
              <th>Note</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map(t => (
              <tr key={t.id}>
                <td>{t.id}</td>
                <td>{t.productId}</td>
                <td>{t.type}</td>
                <td>{t.amount}</td>
                <td>{new Date(t.date).toLocaleString()}</td> {/* Format date */}
                <td>{t.note || 'N/A'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
