import React, { useState, useEffect, useCallback } from 'react';
import api from '../api';

function Sales() {
  const [products, setProducts] = useState([]);
  const [salesData, setSalesData] = useState([]);
  const [filterDate, setFilterDate] = useState('');
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const [receipt, setReceipt] = useState(null);

  const fetchProducts = useCallback(async () => {
    try {
      const res = await api.get('/products');
      setProducts(res.data || []);
    } catch (error) {
      console.error('Failed to fetch products:', error);
    }
  }, []);

  const fetchSales = useCallback(async () => {
    try {
      const url = filterDate ? `/sales?date=${filterDate}` : '/sales';
      const res = await api.get(url);
      setSalesData(res.data || []);
    } catch (error) {
      console.error('Failed to fetch sales:', error);
    }
  }, [filterDate]);

  useEffect(() => {
    setLoading(true);
    Promise.all([fetchProducts(), fetchSales()]).finally(() => setLoading(false));
  }, [fetchProducts, fetchSales]);

  const dailySummary = products.map(product => {
    const sold = salesData
      .filter(sale => sale.productId === product.id)
      .reduce((sum, sale) => sum + sale.quantity, 0);

    const stockLeft = product.stock - sold;
    return {
      ...product,
      sold,
      stockLeft,
      revenue: sold * product.price,
    };
  });

  const totalRevenue = dailySummary.reduce((sum, p) => sum + p.revenue, 0);
  const totalStockLeft = dailySummary.reduce((sum, p) => sum + p.stockLeft, 0);

  const availableStock = selectedProduct
    ? products.find(p => p.id === selectedProduct)?.stock ?? 0
    : 0;

  const sellProduct = async (e) => {
    e.preventDefault();
    if (!selectedProduct || quantity <= 0) {
      alert('Please select a product and enter a valid quantity.');
      return;
    }
    const product = products.find(p => p.id === selectedProduct);
    if (!product) {
      alert('Selected product not found.');
      return;
    }
    if (quantity > product.stock) {
      alert('Insufficient stock.');
      return;
    }

    try {
      const saleAmount = product.price * quantity;
      const saleData = {
        productId: selectedProduct,
        name: product.name,
        quantity,
        amount: saleAmount,
        date: new Date().toISOString().split('T')[0],
      };

      await api.post('/sales', saleData);

      setProducts(prev =>
        prev.map(p =>
          p.id === selectedProduct ? { ...p, stock: p.stock - quantity } : p
        )
      );

      setSalesData(prev => [...prev, saleData]);
      setSelectedProduct('');
      setQuantity(1);
      setReceipt(saleData);
    } catch (error) {
      console.error('Error processing sale:', error);
      alert('Failed to record sale. Please try again.');
    }
  };

  if (loading) return <div className="dashboard">Loading...</div>;

  return (
    <div className="dashboard">
      <div className="card">
        <h3>Sales Overview</h3>
        <label>
          Filter by Date:{' '}
          <input
            type="date"
            value={filterDate}
            onChange={e => setFilterDate(e.target.value)}
          />
        </label>
        <table className="table" style={{ marginTop: 10 }}>
          <thead>
            <tr>
              <th>Product</th>
              <th>Initial Stock</th>
              <th>Sold</th>
              <th>Stock Left</th>
              <th>Revenue</th>
            </tr>
          </thead>
          <tbody>
            {dailySummary.map(p => (
              <tr key={p.id} className={p.stockLeft < 10 ? 'low' : ''}>
                <td>{p.name}</td>
                <td>{p.stock}</td>
                <td>{p.sold}</td>
                <td>{p.stockLeft}</td>
                <td>{`M${p.revenue.toFixed(2)}`}</td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <td><strong>Totals</strong></td>
              <td>-</td>
              <td>-</td>
              <td><strong>{totalStockLeft}</strong></td>
              <td><strong>{`M${totalRevenue.toFixed(2)}`}</strong></td>
            </tr>
          </tfoot>
        </table>
      </div>

      <div className="card" style={{ marginTop: 20 }}>
        <h3>Sell Product</h3>
        <form onSubmit={sellProduct}>
          <select
            value={selectedProduct}
            onChange={e => {
              setSelectedProduct(e.target.value);
              setQuantity(1);
            }}
            required
          >
            <option value="">Select a Product</option>
            {products.map(product => (
              <option
                key={product.id}
                value={product.id}
                disabled={product.stock === 0}
              >
                {product.name} (Stock: {product.stock})
              </option>
            ))}
          </select>

          <input
            type="number"
            min="1"
            max={availableStock}
            value={quantity}
            onChange={e => {
              const val = Math.min(Math.max(1, parseInt(e.target.value) || 1), availableStock);
              setQuantity(val);
            }}
            required
            style={{ marginLeft: 10, width: 80 }}
          />

          <button
            type="submit"
            style={{ marginLeft: 10 }}
            disabled={availableStock === 0}
          >
            Sell
          </button>

          {selectedProduct && (
            <span style={{ marginLeft: 10 }}>
              Available: {availableStock}
            </span>
          )}
        </form>
      </div>

      {receipt && (
        <div
          className="card receipt"
          style={{
            marginTop: 20,
            padding: 15,
            border: '1px solid #ddd',
            maxWidth: 400
          }}
        >
          <h4>Receipt</h4>
          <p><strong>Date:</strong> {new Date(receipt.date).toLocaleDateString()}</p>
          <p><strong>Product:</strong> {receipt.name}</p>
          <p><strong>Quantity:</strong> {receipt.quantity}</p>
          <p>
            <strong>Total Amount:</strong> {`M${receipt.amount.toFixed(2)}`}
          </p>
          <hr />
          <p>Thank you for your purchase!</p>
          <button onClick={() => setReceipt(null)} style={{ marginTop: 10 }}>
            Close Receipt
          </button>
        </div>
      )}
    </div>
  );
}

export default Sales;
