import React, { useEffect, useState } from 'react';
import api from './api';
import Dashboard from './components/Dashboard';
import Navbar from './components/Navbar';
import Reports from './components/Reports';
import Sales from './components/Sales';
import Inventory from './components/Inventory';
import Footer from './components/Footer';
import logo from './assets/logo.png';

function App() {
  const [products, setProducts] = useState([]);
  const [transactions, setTransactions] = useState([]); // Kept for Reports
  const [activeSection, setActiveSection] = useState('dashboard');
  const [refreshSignal, setRefreshSignal] = useState(0);

  const fetchData = async () => {
    try {
      const [productsRes, transactionsRes] = await Promise.all([
        api.get('/products'),
        api.get('/transactions'),
      ]);
      setProducts(productsRes.data || []);
      setTransactions(transactionsRes.data || []);
    } catch (error) {
      console.error('Failed to fetch data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleRefresh = () => {
    setRefreshSignal(prev => prev + 1);
  };

  return (
    <div className="app" style={{ minHeight: '100vh', position: 'relative', paddingBottom: '60px' }}>
      <Navbar logo={logo} setActiveSection={setActiveSection} />
      <div className="main-content">
        <div className="content">
          {activeSection === 'dashboard' && <Dashboard products={products} />}
          {activeSection === 'inventory' && <Inventory onChanged={handleRefresh} />}
          {activeSection === 'reports' && <Reports refreshSignal={refreshSignal} transactions={transactions} />}
          {activeSection === 'sales' && <Sales products={products} />}
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
