import React, { useState, useEffect } from 'react';
import api from '../api';

function Reports({ refreshSignal }) {
  const [products, setProducts] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchData = async () => {
    try {
      const [productsRes, transactionsRes] = await Promise.all([
        api.get('/products'),
        api.get('/transactions'),
      ]);
      setProducts(productsRes.data || []);
      setTransactions(transactionsRes.data || []);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [refreshSignal]); // Refetch when refreshSignal changes

  const totalQuantityAvailable = products.reduce((sum, p) => sum + Number(p.quantity || 0), 0);
  const totalSold = transactions
    .filter(t => t.type === 'deduct' && t.note === 'sold')
    .reduce((sum, t) => sum + Number(t.amount || 0), 0);
  const totalRevenue = transactions
    .filter(t => t.type === 'deduct' && t.note === 'sold')
    .reduce((sum, t) => {
      const product = products.find(p => p.id === t.productId);
      return sum + (product ? Number(product.price || 0) * Number(t.amount || 0) : 0);
    }, 0);

  if (loading) return <div className="dashboard">Loading reports...</div>;

  return (
    <div className="dashboard card">
      <h3>Reports Overview</h3>
      <div className="summary-cards" style={{ display: 'flex', gap: '1rem' }}>
        <div className="summary-card" style={{ flex: 1 }}>
          <h4>Total Quantity Available</h4>
          <p><strong>{totalQuantityAvailable}</strong></p>
        </div>
        <div className="summary-card" style={{ flex: 1 }}>
          <h4>Total Sold</h4>
          <p><strong>{totalSold}</strong></p>
        </div>
        <div className="summary-card" style={{ flex: 1 }}>
          <h4>Total Revenue (M)</h4>
          <p><strong>{totalRevenue.toFixed(2)}</strong></p>
        </div>
      </div>
    </div>
  );
}

export default Reports;
