import React, { useEffect, useState } from 'react';
import api from './api';
import AddProductForm from './components/AddProductForm';
import ProductList from './components/ProductList';
import Dashboard from './components/Dashboard';

function App() {
  const [mergedProducts, setMergedProducts] = useState([]);
  const [uiData, setUIData] = useState([]);

  const fetchProducts = async () => {
    const res = await api.get('/products');
    const staticProducts = res.data || [];
    const savedUIData = localStorage.getItem('uiData') ? JSON.parse(localStorage.getItem('uiData')) : [];
    setUIData(savedUIData);

    // Merge static and UI data, avoiding duplicates by id or name
    const merged = [...staticProducts];
    savedUIData.forEach(uiProduct => {
      if (!merged.some(p => p.id === uiProduct.id || (p.name === uiProduct.name && p.price === uiProduct.price))) {
        merged.push({ ...uiProduct, id: uiProduct.id || Date.now() + Math.random().toString(36).substr(2, 9) });
      }
    });
    setMergedProducts(merged);
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const addProduct = async (newProduct) => {
    try {
      const res = await api.post('/products', newProduct);
      const updatedStatic = res.data;
      const updatedUIData = [...uiData, { ...newProduct, id: updatedStatic.id, timestamp: new Date().toISOString() }];
      setUIData(updatedUIData);
      localStorage.setItem('uiData', JSON.stringify(updatedUIData));
      fetchProducts(); // Refresh merged data
    } catch (err) {
      console.error(err);
      alert('Failed to add to static database, adding to UI only');
      const updatedUIData = [...uiData, { ...newProduct, id: Date.now(), timestamp: new Date().toISOString() }];
      setUIData(updatedUIData);
      localStorage.setItem('uiData', JSON.stringify(updatedUIData));
      fetchProducts();
    }
  };

  const exportUIData = () => {
    const dataStr = JSON.stringify(uiData, null, 2);
    const blob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'uiData.json';
    link.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="container">
      <h1>Boitumelo â€” Inventory</h1>
      <Dashboard products={mergedProducts} />
      <div className="grid">
        <div>
          <AddProductForm onAdded={addProduct} />
          <button onClick={exportUIData} style={{ marginTop: '10px' }}>Export UI Data</button>
        </div>
        <div>
          <ProductList products={mergedProducts} onChanged={fetchProducts} />
        </div>
      </div>
    </div>
  );
}

export default App;
